INSERT INTO `article` (`id`, `title`, `categoryId`, `tagIds`, `source`, `sourceUrl`, `publishedTime`, `body`, `thumb`, `originalThumb`, `picture`, `status`, `hits`, `featured`, `promoted`, `sticky`, `postNum`, `upsNum`, `userId`, `createdTime`, `updatedTime`) VALUES (1,'如何进行资讯设置？',1,'|2|','','',1432210097,'<p><img alt=\"\" src=\"/files/default/2015/05-22/11504228be67930584.jpg?5.5.8\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u><strong><span style=\"font-size:16px\"><a href=\"#1\"><span style=\"color:#33cc99\">一、资讯频道设置</span></a></span></strong></u></p>\r\n\r\n<p><u><strong><span style=\"font-size:16px\"><a href=\"#2\"><span style=\"color:#33cc99\">二、资讯栏目（分类）管理</span></a></span></strong></u></p>\r\n\r\n<p><u><strong><span style=\"font-size:16px\"><a href=\"#3\"><span style=\"color:#33cc99\">三、发布新资讯</span></a></span></strong></u></p>\r\n\r\n<p><u><a href=\"#4\"><span style=\"color:#33cc99\"><strong><span style=\"font-size:16px\">四、资讯管理</span></strong></span></a></u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"#\" name=\"1\"><strong><span style=\"color:#33cc99\"><span style=\"font-size:16px\">一、资讯频道设置</span></span></strong></a></p>\r\n\r\n<p>路径：【管理后台】-【系统】-【运营设置】-【资讯】</p>\r\n\r\n<p>操作：</p>\r\n\r\n<p>1、设置资讯频道名称，该名称将显示在网校资讯首页，网校资讯首页地址：http://www.&times;&times;&times;.com/article ；如果想在网校首页展示资讯，可在【管理后台】-【系统】-【站点设置】-【顶部导航】</p>\r\n\r\n<p>2、列表每页资讯数，自定义网校每页资讯的显示数量，建议不要太少也不要太多，在15~30之间，一页显示太多资讯页面将会拉长。</p>\r\n\r\n<p><img alt=\"\" src=\"/files/default/2015/05-21/20095537570a299802.png?5.5.8\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"#\" name=\"2\"><span style=\"color:#33cc99\"><span style=\"font-size:16px\"><strong>二、资讯栏目（分类）管理</strong></span></span></a></p>\r\n\r\n<p>路径：【管理后台】-【运营】-【资讯管理】-【栏目管理】</p>\r\n\r\n<p>操作：</p>\r\n\r\n<p>1、新建栏目；</p>\r\n\r\n<p>2、编辑已有栏目；</p>\r\n\r\n<p>3、给一级栏目添加子栏目。</p>\r\n\r\n<p><img alt=\"\" src=\"/files/default/2015/05-22/1119059ab5c8760755.png?5.5.8\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"#\" name=\"3\"><span style=\"color:#33cc99\"><strong><span style=\"font-size:16px\">三、发布新资讯</span></strong></span></a></p>\r\n\r\n<p>路径：【管理后台】-【运营】-【资讯管理】右上方有【添加资讯】按钮。</p>\r\n\r\n<p><img alt=\"\" src=\"/files/default/2015/05-21/2021051688b6814367.png?5.5.8\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"#\" name=\"4\"><span style=\"color:#33cc99\"><strong><span style=\"font-size:16px\">四、资讯管理</span></strong></span></a></p>\r\n\r\n<p>路径：【管理后台】-【运营】-【资讯管理】</p>\r\n\r\n<p>操作：</p>\r\n\r\n<p>1、编辑修改已添加资讯；</p>\r\n\r\n<p>2、取消发布资讯；</p>\r\n\r\n<p>3、将资讯移至回收站，移至回收站的资讯可选择永久删除；</p>\r\n\r\n<p>4、修改资讯属性</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">置顶：设为&ldquo;置顶&rdquo;后，该资讯将会显示在列表的最前面。</span></li>\r\n	<li><span style=\"font-size:14px\">头条：设为&ldquo;头条&rdquo;后，如果正文含有图片，则该资讯的第一张图片将显示在资讯首页的头条图片区，图片尺寸建议统一为：720*400。</span></li>\r\n	<li><span style=\"font-size:14px\">推荐：设为&ldquo;推荐&rdquo;后，该资讯将会显示在页面右侧的推荐资讯中。</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/files/default/2015/05-22/1127346ef0cc180145.png?5.5.8\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n','','','/files/default/2015/05-22/11504228be67930584.jpg?5.5.8','published',11,1,1,1,2,1,1,1432210936,1434020425),(2,'我国MOOC平台技术问题研究',2,'||','科技与出版','',1432287832,'<div>&nbsp;</div>\r\n\r\n<div>\r\n<div>\r\n<h3><strong>1&nbsp;我国高等教育MOOC平台建设现状</strong></h3>\r\n\r\n<p>互联网、&nbsp;Web2.0、大数据挖掘、&nbsp;音视频、&nbsp;人工智能等信息技术的发展共同促成了MOOC[EDUSOHO慕课]时代的到来。音视频技术的成熟使MOOC[EDUSOHO慕课]在线课程制作成为可能；互联网和移动互联网的普及使人们可以随时随地学习MOOC[EDUSOHO慕课]课程；&nbsp;Web2.0时代的到来使MOOC[EDUSOHO慕课]平台具有了交互性，学习者可以得到更好的用户体验；大数据挖掘技术有利于平台个性化学习功能的建设；人工智能技术节省了教师批改作业的大量时间。教育与信息技术的结合，迎来了教育领域的互联网式变革。</p>\r\n\r\n<p>随着信息技术的发展以及网络的普及，我国各大高校纷纷跻身MOOC[EDUSOHO慕课]平台建设之列。高校MOOC[EDUSOHO慕课]平台的建设，使优质教育资源得到了最大化的利用与共享。我国高校开发的MOOC[EDUSOHO慕课]平台主要有：爱课程网、&nbsp;上海高校课程资源共享平台、&nbsp;东西部高校课程共享联盟、学堂在线、&nbsp;ewant、&nbsp;好大学在线、&nbsp;中国地方高校MOOC[EDUSOHO慕课]联盟等。</p>\r\n\r\n<p>清华大学等国内名校通过MOOC[EDUSOHO慕课]共享课程平台的开放，使更多人可以去学习名校名师的课程，将课程传播得更广，从而减少了地区与校际之间的教育不平衡性。而一些同层次高校课程联盟的建设则降低了课程制作费用，提高了课程制作效率。各高校将自己富有特色的优质课程放在MOOC[EDUSOHO慕课]平台上供各联盟高校共享，并且学分互认，不仅加强了校际的沟通与交流，而且节省了教育资源。</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h3><strong>2&emsp; MOOC平台建设的技术性要求</strong></h3>\r\n\r\n<p>MOOC[EDUSOHO慕课]将教育技术与信息技术完美融合，&nbsp;是教育史上又一次重要的变革。正如北京大学李晓明教授所言，&nbsp;看似&nbsp;MOOC[EDUSOHO慕课]没有技术上的惊艳表现，&nbsp;但它把各种现有技术很好地整合在了一起，产生了一个集成创新产品。从技术层面来看，MOOC[EDUSOHO慕课]具有完全开放、支持海量资源和用户、多元交互与协作、全程记录与评价等功能。一个优质的MOOC[EDUSOHO慕课]平台需要全面展现&ldquo;massive open online courses&rdquo;&nbsp;的内涵。在大规模&nbsp;（massive）&nbsp;方面，&nbsp;首先，&nbsp;要保证平台资源的安全性和稳定性，&nbsp;并将多样化的学习资源标准化处理。笔者在学堂在线等交流论坛发现，有部分学习者反映课程界面无法正常打开等问题，这将直接导致用户流失。其次，在面对大规模用户时，云计算等大数据挖掘技术的运用在整个平台运营过程中都十分重要。在开放（open）方面，&nbsp;整个平台设计应尽量人性化，以方便学习者注册、&nbsp;登录、&nbsp;学习、交流；&nbsp;同时应善于利用微博、&nbsp;QQ、&nbsp;微信等社交软件，&nbsp;增强学习者的交互性；&nbsp;此外，&nbsp;为了能更好地提供个性化学习功能，&nbsp;对学习者学习行为的记录也十分必要。在在线&nbsp;（online）方面，&nbsp;平台应提供学生互动的空间，有的平台会在在线视频的界面提供相应的讨论板块，学习者可以在看视频的同时，&nbsp;与同学即时讨论课程相关内容；&nbsp;为了提升学习者的学习效果，&nbsp;平台还应构建多样化的评价系统，&nbsp;让学习者更好地参与课程。在课程&nbsp;（courses）&nbsp;设置上，&nbsp;MOOC[EDUSOHO慕课]首先应区别于传统的网络课堂，&nbsp;在课堂呈现上应追求&nbsp;&ldquo;面对面&rdquo;&nbsp;的教学效果；此外，&nbsp;还应追踪学习者的课程情况以及时调整教学进度。</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h3><strong>3&nbsp;我国高等教育MOOC平台建设及技术问题</strong></h3>\r\n\r\n<h4><strong>3.1&nbsp;基础设施落后</strong></h4>\r\n\r\n<p>首先，由于我国高校信息化水平不均衡，呈现区域化的典型特征，我国的MOOC[EDUSOHO慕课]平台多由经济发达地区的高校建设而成，而MOOC[EDUSOHO慕课]课程的最大需求者却是西部地区的学生。其次，东西部经济条件的差异，导致西部地区用户群的终端设备普及率较低。此外，偏远地区网络设施建设落后也是阻碍MOOC[EDUSOHO慕课]在我国平衡发展的一大问题。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.2&nbsp;学生评价系统不完善</strong></h4>\r\n\r\n<p>MOOC[EDUSOHO慕课]是一种大规模的在线课程，&nbsp;学生和教师之间无法进行面对面的交流；&nbsp;由于其大规模的特性，&nbsp;老师也无法像传统教育那样细致地去考核每一个学生。如何使针对大规模学生的评价系统对每一位学生作出正确公正的评价，是MOOC[EDUSOHO慕课]良好发展的关键点。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.3&nbsp;个性化学习模式不显著</strong></h4>\r\n\r\n<p>个性化教学是教育发展的需要和必然要求。MOOC[EDUSOHO慕课]学习的人群，包括不同年龄、&nbsp;职业、&nbsp;学历、专业知识背景等各种各样的学生。如何根据不同的学生来设置不同难度要求和进度安排的个性化学习模式，是MOOC[EDUSOHO慕课]平台建设需要解决的重要问题。在这样一个平台中，每一位学生的学习路径、进展都是不一样的。MOOC[EDUSOHO慕课]学习者应该能够根据自己的学习背景和学习目的，决定个性化的参与路径，选择和定制个性化的课程。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.4&nbsp;大数据挖掘技术不成熟</strong></h4>\r\n\r\n<p>大数据时代的到来，使MOOC[EDUSOHO慕课]的全球化发展成为可能。MOOC[EDUSOHO慕课]因其大规模性，其课程设计需建立在对学习者课程学习的路径、规律、&nbsp;学习倾向等大规模数据研究分析的基础之上，才能设计出更加完备的课程教学体系。目前，&nbsp;国内MOOC[EDUSOHO慕课]平台在大数据挖掘技术方面仍存在不足。如何利用大数据挖掘技术来研究分析从而形成成熟的课程体系，是我国高等教育MOOC[EDUSOHO慕课]平台必须解决的技术性问题。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.5&nbsp;学习社交性不强</strong></h4>\r\n\r\n<p>MOOC[EDUSOHO慕课]与传统在线课堂的最大区别就是，MOOC[EDUSOHO慕课]课程是实时交互的。社交化是MOOC[EDUSOHO慕课]发展的一大趋势，Web2.0技术的成熟催生了MOOC[EDUSOHO慕课]平台的交互性，使学习者能够得到更好的用户体验。理想的MOOC[EDUSOHO慕课]学习环境就是在充分保证学习行为的个体性的前提下，营造一种群体性学习的氛围，让学习变得更加有趣。我国高等教育MOOC[EDUSOHO慕课]课程不能仅仅满足于把课程搬到网络上，而丢失了传统课堂的社交功能。如何让学习者在MOOC[EDUSOHO慕课]课堂中获得实体课堂的交流、讨论等交互体验，是MOOC[EDUSOHO慕课]平台建设的重点内容之一。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.6&nbsp;课程缺乏游戏性模式</strong></h4>\r\n\r\n<p>培养学生的学习兴趣，创造出一种全新的以游戏为基础的学习方式，它属于深度学习，同时又能让学生有身临其境之感，甚至会导致上瘾。教育游戏化能激发学习者的潜能，引导学习者主动参与学习。目前，国内MOOC[EDUSOHO慕课]平台的课程，很少将游戏模式融入课程之中。在课程没有学分、证书等激励机制的情况之下，学习者很容易就会放弃一门课程。如何在课程中开发游戏应用，是平台方吸引并壮大用户群的重要手段。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>3.7&nbsp;移动客户端应用单一</strong></h4>\r\n\r\n<p>随着通信技术和网络技术的迅速发展，智能移动终端越来越普及。手机、&nbsp;Pad等移动终端让学习者可以在任意时间、任意地点通过移动网络获取学习内容，这在很大程度上满足了学习者对泛在学习的诉求。MOOC[EDUSOHO慕课]学习者可自由选择授课时间以及短视频的授课内容，使得MOOC[EDUSOHO慕课]平台开发移动应用势在必行，&nbsp;MOOC[EDUSOHO慕课]学习者可以利用碎片时间完成课程内容的学习和巩固。目前，我国MOOC[EDUSOHO慕课]平台移动客户端一般只有观看课程的功能，暂时还没有作业、测验、&nbsp;考试等其他应用。</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h3><strong>4&emsp; MOOC平台技术问题解决思路</strong></h3>\r\n\r\n<h4><strong>4.1&nbsp;强化基础设施建设</strong></h4>\r\n\r\n<p>课程资源、互联网和计算机设备是开展MOOC[EDUSOHO慕课]课程最基本的条件。我国高等教育MOOC[EDUSOHO慕课]平台应打破校际藩篱，将自己最优质的课程置于平台让各高校共享。网络建设是一个庞大的项目，需要政府的参与与支持。计算机设备的配置则应由政府和MOOC[EDUSOHO慕课]供应商共同解决。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>4.2&nbsp;完善学生评价系统</strong></h4>\r\n\r\n<p>MOOC[EDUSOHO慕课]平台的学生评价包括过程性评价和总结性评价两部分。</p>\r\n\r\n<p>一方面，在过程性评价方面，平台不仅要考核学习者整个学习过程的作业、测验等环节，还要考虑其诚信等问题。所以，&nbsp;在课程评价中不能简单地给出作业成绩，&nbsp;还应采取智能化评价手段将评价结果及时反馈给学生，&nbsp;使评价在学生整个学习过程中起到促进作用，&nbsp;从而提升学习效果。此外，&nbsp;在学生上课、&nbsp;做作业、&nbsp;做小测验的过程中，&nbsp;需应用技术手段去识别学习者是否为本人。笔者在使用国内MOOC[EDUSOHO慕课]平台时，尚未发现有平台在学习者识别方面采取相关手段。</p>\r\n\r\n<p>另一方面，在总结性评价方面，如何调节评估模式关系着学习者成绩的公正性。目前MOOC[EDUSOHO慕课]平台主要有机器评估和人工评估两种模式。机器评估的方式可对学生作出自动评价和反馈，如edX的自动作文评分（AES）技术。虽然机器评估可以实现极高的效率，但是机器没有主观意识，只能根据程序设定的唯一标准答案进行判定，而容易对同样正确的其他答案产生误判。人工评估主要有学生自评和同伴互评两种形式。同伴互评机制可减轻教师的工作量，同时让学生化被动为主动，积极参与到MOOC[EDUSOHO慕课]课程中来。但相较于机器评估而言，人工评估的工作量大很多。</p>\r\n\r\n<p>MOOC[EDUSOHO慕课]平台在构建评价体系时，&nbsp;应提供多重评价方式的技术和手段。根据课程性质来分配评估方式，&nbsp;并且做好评估过程中的防作弊等相关工作，&nbsp;使评价结果真实有效，&nbsp;切实提升在线学习的效果。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>4.3&nbsp;构建个性化学习模式</strong></h4>\r\n\r\n<p>学堂在线在部分课程中，将云计算、&nbsp;数据科学、&nbsp;创业管理等板块设置成入门、&nbsp;进阶、&nbsp;探索三个由易到难的阶段，&nbsp;由学习者根据自己的学习水平来选择路径。ewant育网则在每门课程的后面注明&nbsp;&ldquo;先修科目或先备能力&rdquo;&nbsp;，&nbsp;让学习者在选择课程之前对课程的难易度有所了解。目前国内的MOOC[EDUSOHO慕课]平台基本上都是单一的课程发布，很少针对学习者创建个性化的学习系统。国内高等教育MOOC[EDUSOHO慕课]平台应利用大数据技术挖掘分析学生的学习信息，并创建学生学习数据库，为在线学习者提供个性化的支持服务，使学习者高效快速地获取所需信息，能够获得最大的教育效益。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>4.4&nbsp;灵活运用大数据挖掘技术</strong></h4>\r\n\r\n<p>应用大数据技术，可以对课程进展和学习行为的海量信息进行分析和挖掘，对课程学习过程、学习方式等实现高效的聚类、分类、&nbsp;关联分析，&nbsp;形成对课程和学习者行为的分析与预测，&nbsp;为课程改进提供参考，&nbsp;从而形成科学系统的网络教学结构，避免知识结构零散、&nbsp;体系不完整、&nbsp;课程混乱等弊端。我国高等教育MOOC[EDUSOHO慕课]平台应充分利用云计算、&nbsp;大数据挖掘等技术，&nbsp;将海量数据作为平台课程建设的有效依据，&nbsp;形成个性化、&nbsp;社交化等课程特色。我国最大的MOOC[EDUSOHO慕课]平台&nbsp;&ldquo;学堂在线&rdquo;&nbsp;通过大数据开发，&nbsp;深入挖掘海量学习行为数据，&nbsp;为合作高校和相关开课老师提供分析报告和研究服务，&nbsp;全面跟踪和掌握学生的学习行为、&nbsp;学习过程和学习特点，&nbsp;开展有针对性的教学，&nbsp;提高学生的学习质量和学习效率。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>4.5&nbsp;建设社交性学习模式</strong></h4>\r\n\r\n<p>首先，&nbsp;MOOC[EDUSOHO慕课]平台的同伴互评机制即属于社交性学习模式，能够促进学习者之间的交流。此外，&nbsp;MOOC[EDUSOHO慕课]应充分应用Web2.0&nbsp;的理念，将Web2.0技术如电子邮件、博客、Facebook、&nbsp;Wiki&nbsp;等作为学生讨论和建构学习内容的技术工具。学生不仅成了内容的消费者，而且成了内容的开发者和创建者。学习者在友好的学习氛围中协作、分享学习成果，根据兴趣自发组织学习圈，&nbsp;随时随地组织学习。</p>\r\n\r\n<p>MOOC[EDUSOHO慕课]平台要充分利用交互技术，&nbsp;使师生之间可以很方便地通过发帖、&nbsp;私信等方式进行教学交流。社交性的学习模式可以让大家在通过MOOC[EDUSOHO慕课]平台学习课程之后，在平台提供的讨论区或者其他博客、微博、&nbsp;QQ、&nbsp;MOOC[EDUSOHO慕课]学院等社交工具上继续学习交流，集思广益。此外，平台应该为每个学生创建一个个人主页，通过个人主页，&nbsp;学生可以积极参与其他同学的讨论与活动。</p>\r\n\r\n<h4>&nbsp;</h4>\r\n\r\n<h4><strong>4.6&nbsp;优化教育游戏化模式</strong></h4>\r\n\r\n<p>游戏其实也是一种评价学生的方式。教育游戏的开发，可以使学生在玩游戏的同时进行解题、测试、评估。台湾大学电机系教师叶丙成在其《概率与统计》&nbsp;课上，&nbsp;开发了一款BJ-Online线上游戏，学生在游戏的过程中解题，&nbsp;游戏的乐趣更能激发学习者的学习兴趣。各大高校在开展MOOC[EDUSOHO慕课]课程时，应尽量去挖掘课程的可游戏性，设计游戏模式，&nbsp;让学习者从被动接受者转变成主动出击者。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><strong>4.7&nbsp;加强移动客户端的应用开发</strong></h4>\r\n\r\n<p>目前，国内的学堂在线、爱课程网、&nbsp;智慧树等MOOC[EDUSOHO慕课]平台都开发了IOS版和Android版的App，使用HTML5技术可以方便地支持移动设备。但是在MOOC[EDUSOHO慕课]平台移动端上，&nbsp;只能进行微课程的视频观看，&nbsp;暂时无法完成课后作业和测验。所以在进行移动端课程建设的同时，也应跟进作业、&nbsp;测验等的移动端功能建设。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>5&nbsp;结语</strong></h3>\r\n\r\n<p>MOOC[EDUSOHO慕课]的发展对国内外高等教育产生了深刻的影响。国内高校对MOOC[EDUSOHO慕课]建设的重视，不仅有利于提升我国高等教育的质量和效率，更有利于我国高等教育国际化的推进和实现。借助信息技术促进MOOC[EDUSOHO慕课]平台的发展，从而转变我国高等教育的办学理念，变革教学方式和学生培养模式。从技术平台、内容优化、&nbsp;环境互动、&nbsp;融合发展等多层面逐步跟上世界最新发展趋势，&nbsp;提高我国高校参与国际化MOOC[EDUSOHO慕课]建设的共建共享意识和科学运营水平。教育部高教司司长张大良表示，教育部将加强研究在线课程与现实课程的等效互认、校际间学分互认、证书发放等方面的制度，以跟上网络教学变革的步伐，这给我国高等教育MOOC[EDUSOHO慕课]平台的发展打了一针强心剂。</p>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>\r\n<p>&nbsp;</p>\r\n\r\n<p><em><a href=\"http://www.edusoho.com/\">EduSoho开源网络课堂</a></em>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><em><a href=\"file:///C:/Users/genius/Desktop/EduSoho%E6%85%95%E8%AF%BE%E7%89%88\">EduSohoMOOC版</a></em>，中国的MOOC！http://www.edusoho.com/product/mooc</p>\r\n</div>\r\n</div>\r\n','','','','published',1,0,1,0,0,0,1,1432287917,1432288740),(3,'MOOC平台将成为高校的标配',1,'||','','',1432287954,'<div>&nbsp;</div>\r\n\r\n<div>\r\n<p>近几年，国外的MOOC市场发展得火热，而国内虽然有清华、上海交大等作出尝试，但相对来说就冷清得多，究其原因，主要是国内高校还处于体制内管理，在学分认定等MOOC发展的关键点受到了限制。但是近日教育部的发布的一纸意见，让状况变得不同！</p>\r\n\r\n<p><strong>制度创新推动MOOC市场发展</strong></p>\r\n\r\n<p>近日，教育部发布《<a href=\"http://m.moe.gov.cn/wj/zywj/201504/t20150428_2682.html?from=groupmessage&amp;isappinstalled=0\" target=\"_blank\">教育部关于加强高等学校在线开放课程建设应用与管理的意见</a>》，意见中重点提到了要建设在线开放课程公共服务平台。<strong>鼓励公共服务平台与国家开放大学教学平台开展合作</strong>，为终身教育提供优质课程。以及推进在线开放课程学<strong>分认定和学分管理制度创新</strong>。在保证教学质量的前提下，鼓励高校开展在线学习、在线学习与课堂教学相结合等多种方式的学分认定、学分转换和学习过程认定。可以预见，学分认定等问题的解决，将引起国内MOOC市场的剧变！</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>MOOC</strong><strong>对中国高校的意义</strong></p>\r\n\r\n<p>在我国，共有2542所高等学校（包括民办普通高校444所），但是相较于参加高考的人数仍远远不足。去年，我国高考考生达到939万，而录取人数不到700万，这意味着有200多万人没有大学可读。他们不是自愿的，只是在千万大军过独木桥的时候，不幸落榜。但是这似乎很难去责怪高校们，700万高考考生，平均每所高校要承载近3000人，这不是件容易的事情。据2014年底教育部的统计，全国高校教师数量不到20万，而学生与老师的比例高达14：1到16：1。</p>\r\n\r\n<p>如何解决学生上大学的问题已经讨论过太久太久，单纯的增加教师数量和开设更多高校似乎并不是个好办法。随着MOOC的兴起，我们似乎看到了一种可能性----通过MOOC教学来让更多的学生享受到教育。而搭建一个MOOC平台，开展MOOC教学，将释放老师更多的能量，通过MOOC平台来管理学生的学习情况，跟踪学生的学习数据，也将使学校能够承载更多的学生，并能帮助学生更好的进行学习，无论是创收还是高校软硬实力的提升都是大有裨益。</p>\r\n\r\n<p>清华大学计算机系的系主任孙老师分享过一个主题&ldquo;以MOOC支撑一个完整的高水平本科专业课程体系：一种现实可能性&rdquo;。他拿出清华大学本科生的整个教学培养体系，然后一一去找向对应的课程对应。后来发现，目前为止95%以上的课程都可以找到MOOC课程去对应。某种意义上意味着，清华大学计算机系本科的学生整个完整的教学现在就可以通过MOOC的平台去实现。从这个角度来讲，计算机专业只是一个开始，相信随着MOOC课程的完善，这里面最终影响的可不仅仅是计算机专业的学生，也不仅仅是高校自己，甚至其他的院校、其他的机构都会受到一定的影响，这个影响甚至大到我们无法想象。</p>\r\n\r\n<p>这不禁让笔者想起曾经看过的一篇文章，《MOOC革命将造成的中国高校兼并与消亡》，其中一些预想或许真的会成为现实。</p>\r\n\r\n<p>不过换个角度来说，对于高校而言，通过MOOC的教学模式，吸收更多的MOOC资源将是提升实力的一大途径。既然清华大学可以通过MOOC平台来实现完整的教学，那么其他的院校们也可以。针对本校学生，设置更具有特色，更适合他们的课程。所以MOOC平台将成为高校的标配，用自己的MOOC平台来展现自身的特色，用自己的MOOC平台服务好自己的学生才能不被这时代的MOOC洪流吞没。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>MOOC</strong><strong>技术服务市场利好</strong></p>\r\n\r\n<p>由于MOOC平台的首个特性就是&ldquo;大规模&rdquo;，要求大并发量支撑等技术要求，这对技术要求是相当高的，故而对于高校来开展MOOC也是一个不小的挑战。而在教育部发布的《教育部关于加强高等学校在线开放课程建设应用与管理的意见》中指出：&ldquo;发挥我国高等教育教学传统优势，借鉴国际先进经验，采取&ldquo;高校主体、政府支持、社会参与&rdquo;的方式，集聚优势力量和优质资源，构建具有中国特色在线开放课程体系和公共服务平台。&rdquo;</p>\r\n\r\n<p>高校+政府+社会支撑将是开展MOOC的一个有效方式。越来越多企业机构能够为高校们提供搭建平台的服务，国外有edX、coursera这些MOOC巨头可以提供服务，而在国内，我们也有EduSoho慕课系统可供选择，它作为国内首个开源的MOOC系统，更具中国特色。不仅具备常见的慕课所需功能，而且其提出的基于班级的社区化学习理念，更或可解决当前慕课完成率低的问题。</p>\r\n\r\n<p>有了政府的支持，有了社会的参与，高校还有什么理由对MOOC说不呢？而通过以高校主体加上政府鼎力支持以及社会的参与，我们也有理由相信中国的MOOC发展将会给世界一个惊喜。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><a href=\"http://www.edusoho.com/\">EduSoho开源网络课堂</a></em>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><em><a href=\"file:///C:/Users/genius/Desktop/EduSoho%E6%85%95%E8%AF%BE%E7%89%88\">EduSoho慕课版</a></em>，中国的MOOC！http://www.edusoho.com/product/mooc</p>\r\n</div>\r\n','','','','published',5,0,1,1,0,0,1,1432287995,1432288749),(4,'教育部力撑MOOC，MOOC爆发东风已至',2,'||','36kr','',1432288024,'<p>&nbsp;</p>\r\n\r\n<p>近日，教育部发布《教育部关于加强高等学校在线开放课程建设应用与管理的意见》，意见中重点提到了几点：</p>\r\n\r\n<blockquote>1、 建设一批以大规模在线开放课程为代表、课程应用与教学服务相融通的优质在线开放课程。</blockquote>\r\n\r\n<blockquote>2、 认定一批国家精品在线开放课程。2017年前认定1000余门国家精品在线开放课程。到2020年，认定3000余门国家精品在线开放课程。</blockquote>\r\n\r\n<blockquote>3、 建设在线开放课程公共服务平台。鼓励公共服务平台与国家开放大学教学平台开展合作，为终身教育提供优质课程。</blockquote>\r\n\r\n<blockquote>4、 进在线开放课程学分认定和学分管理制度创新。在保证教学质量的前提下，鼓励高校开展在线学习、在线学习与课堂教学相结合等多种方式的学分认定、学分转换和学习过程认定。</blockquote>\r\n\r\n<p>可以想象到的是几个场景发生：</p>\r\n\r\n<p>1、 越来越多的高校投入到MOOC课程的制作中，将会拥有自己独立的MOOC平台。目前，已经有学堂在线（依托清华）、华文慕课（依托北大），好大学在线（依托上海交大）四家，中国大学生MOOC(依托的是爱课程网，是联合30多所985高校共建的。)未来985、211高校也有可能加入其中。</p>\r\n\r\n<p>2、 MOOC课程内容会越来越丰富，除了目前比较火热的人文、计算机、经济管理等课程之外，一些冷僻领域（生物、物理）也有相应优质的符合线上学习的MOOC内容。</p>\r\n\r\n<p>3、 高校将会逐步开放与第三方平台的合作，进行课程托管，而对于一些第三方的平台（网易云课堂、顶你学堂、慕课网）来说，不仅解决了课程来源的问题，同时能够快速在学校推广。</p>\r\n\r\n<p>4、 开放学分认证和管理，将会促使MOOC越来越成为大学生日常学习的一个方式，也会解决目前MOOC课程结课率不高的问题。</p>\r\n\r\n<p>5、 围绕着MOOC生态的产品会越来越多，例如MOOC录制工具，MOOC的云端存储，接入MOOC平台的评测工具，以及针对MOOC学习社区产品（例如Edusoho）。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>附：<strong>《关于加强高等学校在线开放课程建设应用与管理的意见》</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>各省、自治区、直辖市教育厅（教委），新疆生产建设兵团教育局，有关部门（单位）教育司（局）,部属各高等学校：</p>\r\n\r\n<p>近年来，大规模在线开放课程（&ldquo;慕课&rdquo;）等新型在线开放课程和学习平台在世界范围迅速兴起，拓展了教学时空，增强了教学吸引力，激发了学习者的学习积极性和自主性，扩大了优质教育资源受益面，正在促进教学内容、方法、模式和教学管理体制机制发生变革，给高等教育教育教学改革发展带来新的机遇和挑战。为加快推进适合我国国情的在线开放课程和平台建设，促进课程应用，加强组织管理，现提出以下意见。</p>\r\n\r\n<p><strong><span style=\"font-size:24px\">一、总体要求</span></strong></p>\r\n\r\n<p><strong><span style=\"font-size:18px\">（一）指导思想</span></strong></p>\r\n\r\n<p>以邓小平理论、&ldquo;三个代表&rdquo;重要思想、科学发展观为指导，深入贯彻习近平总书记系列重要讲话精神，坚持培育和践行社会主义核心价值观，落实教育规划纲要和《教育信息化十年发展规划（2011-2020年）》战略部署，紧紧围绕立德树人的根本任务，遵循教育教学规律，深化高等教育教育教学改革，主动适应学习者个性化发展和多样化终身学习需求，立足国情建设在线开放课程和公共服务平台，加强课程建设与公共服务平台运行监管，推动信息技术与教育教学深度融合，促进优质教育资源应用与共享，全面提高教育教学质量。</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">（二）基本原则</span></strong></p>\r\n\r\n<p>立足自主建设。发挥我国高等教育教学传统优势，借鉴国际先进经验，采取&ldquo;高校主体、政府支持、社会参与&rdquo;的方式，集聚优势力量和优质资源，构建具有中国特色在线开放课程体系和公共服务平台。坚持公益性服务为基础，引入竞争机制，建立在线开放课程和平台可持续发展的长效机制。</p>\r\n\r\n<p>注重应用共享。坚持应用驱动、建以致用，着力推动在线开放课程的广泛应用。整合优质教育资源和技术资源，实现课程和平台的多种形式应用与共享，促进教育教学改革和教育制度创新，提高教育教学质量。</p>\r\n\r\n<p>加强规范管理。坚持依法管理，明确学校和平台运行机构的主体责任，强化建设主体的自我管理机制，规范在线开放课程建设、应用、引进和对外推广的工作程序。完善课程内容审查制度，加强教学过程和平台运行监管，防范和制止有害信息传播，保障平台运行稳定和用户、资源等信息安全。</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">二、重点任务</span></strong><strong><span style=\"font-size:18px\">&nbsp;</span></strong></p>\r\n\r\n<p>（一）建设一批以大规模在线开放课程为代表、课程应用与教学服务相融通的优质在线开放课程。支持具有学科专业优势和现代教育技术优势的高校，以大学生文化素质教育课、受众面广量大的公共课和专业核心课程为重点，建设适合网络传播和教学活动的内容质量高、教学效果好的在线开放课程。鼓励高校间通过协同创新和集成创新的方式建设满足不同教学需要、不同学习需求的在线开放课程或课程群。有组织地建设一批高校思想政治理论课等在线开放课程。</p>\r\n\r\n<p>（二）认定一批国家精品在线开放课程。综合考察课程的教学内容与资源、教学设计与方法、教学活动与评价、教学效果与影响、团队支持与服务等要素，采取先建设应用、后评价认定的方式，2017年前认定1000余门国家精品在线开放课程。到2020年，认定3000余门国家精品在线开放课程。</p>\r\n\r\n<p>（三）建设在线开放课程公共服务平台。在具有良好公益性、开放性的国内已运行平台中，通过申报、专家遴选的方式，选择基础良好、技术先进、符合国情、安全稳定、优质课程资源集聚、服务高效的平台，认定为在线开放课程公共服务平台。鼓励公共服务平台之间实现课程资源和应用数据共享，营造开放合作的网络教学与学习空间。鼓励高校使用在线开放课程公共服务平台。高校也可选用适合本校需求的其他国内平台以及小规模专有在线课程平台，开展在线开放课程建设和应用。鼓励公共服务平台与国家开放大学教学平台开展合作，为终身教育提供优质课程。鼓励平台建设方、高校协同建设和运用在线课程大数据，为高校师生和社会学习者提供优质高效的全方位或个性化服务。</p>\r\n\r\n<p>（四）促进在线开放课程广泛应用。鼓励高校结合本校人才培养目标和需求，通过在线学习、在线学习与课堂教学相结合等多种方式应用在线开放课程，不断创新校内、校际课程共享与应用模式。鼓励承担对口支援任务的高校探索通过在线开放课程支援西部受援高校教学，受援高校应积极应用在线开放课程。鼓励在线开放课程公共服务平台在保障公益性的同时，积极探索课程拓展资源与个性化学习服务的市场化运营方式。</p>\r\n\r\n<p>（五）规范在线开放课程的对外推广与引进。对外推广或引进课程应遵守我国教育、中外合作办学、互联网等相关法律法规，履行我国加入世界贸易组织有关教育服务的具体承诺，并择优推荐选择。学校或平台承担课程对外推广或引进课程的直接责任。鼓励通过在线开放课程公共服务平台和境外平台积极对外推广我国优质课程。鼓励优先引进反映学科发展前沿且具有先进的教育理念和教育经验的自然科学、工程与技术科学等学科优质课程。</p>\r\n\r\n<p>（六）加强在线开放课程建设应用的师资和技术人员培训。依托高校、相关机构、专家组织和在线开放课程公共服务平台，根据教师、学习者的需求变化和技术发展，开展课程建设、课程应用以及大数据分析应用等培训。</p>\r\n\r\n<p>（七）推进在线开放课程学分认定和学分管理制度创新。鼓励高校制订在线开放课程教学质量认定标准，将通过本校认定的在线课程纳入培养方案和教学计划，并制订在线课程的教学效果评价办法和学生修读在线课程的学分认定办法。在保证教学质量的前提下，鼓励高校开展在线学习、在线学习与课堂教学相结合等多种方式的学分认定、学分转换和学习过程认定。</p>\r\n\r\n<p><strong><span style=\"font-size:18px\">三、组织管理</span></strong></p>\r\n\r\n<p>（一）教育部为在线开放课程和公共服务平台的建设提供政策研究、宏观指导和一定的条件支持，协同国家有关部门依据国家网络与信息安全的政策法规履行相应的管理职能。推动有关专家组织和机构开展在线开放课程理论、教学模式与学习方式、课程共享模式、核心技术等研究。组织公共服务平台遴选。组织&ldquo;国家精品在线开放课程&rdquo;认定，并对课程建设予以支持。通过使用评价、定期检查等方式，对国家精品在线开放课程的在线运行、实际应用、教学效果等进行跟踪监测和综合评价。对在线开放课程公共服务平台的网络安全、内容安全、数据安全、运行及服务进行规范管理。省级教育行政部门要鼓励在线开放课程在本区域的建设和应用，给予相应的政策支持，加强对课程建设和平台的监管。</p>\r\n\r\n<p>（二）高校应切实承担在线开放课程建设应用与管理的主体责任。高校领导要深刻认识信息技术高速发展对教育教学的影响，将建设和使用在线开放课程作为推进教育教学改革的重要举措，着力提升广大教师将信息技术与高等教育深度融合的意识、水平和能力，把在线开放课程作为课堂教学的重要补充，根据本校实际建立在线开放课程教学与学习的管理、激励和评价机制，培育一批导向正确、影响力大的网络教学名师。探索建立高校内部或高校之间具备考核标准的在线学习认证和学分认定机制，积极探索并推进在线开放课程的应用，加强课程选用管理及学分管理，确保教学应用质量。课程建设高校作为课程内容和教学活动的责任主体，要将社会主义核心价值观融入课程建设，建立和实施课程建设、质量审查、课程运行保障和效果测评等制度，不断提高课程质量。</p>\r\n\r\n<p>（三）在线开放课程公共服务平台建设方要切实承担课程服务和数据安全保障的主体责任。要严格遵守国家网络与信息安全管理规范，依法依规开展活动，为高校师生和社会学习者提供优质高效的全方位、个性化服务，建立全方位安全保障体系，实施对课程内容、讨论内容、学习过程内容的有效监管，防范和及时制止网络有害信息的传播。要高度重视知识产权保障，与高校、课程建设团队签订平等互利的知识产权保障协议，明确各方权利和义务，切实保障各方权益。</p>\r\n\r\n<p>（四）省级教育行政部门、高校根据本意见，结合本区域、本校实际，对在线开放课程的建设、应用与管理制订实施办法</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/\"><span style=\"color:rgb(255, 0, 0)\">EduSoho开源网络课堂</span></a>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><a href=\"file:///C:/Users/genius/Desktop/EduSoho%E6%85%95%E8%AF%BE%E7%89%88\"><span style=\"color:rgb(255, 0, 0)\">EduSoho慕课版</span></a>，中国的MOOC！http://www.edusoho.com/product/mooc</p>\r\n','','','','published',1,0,1,0,0,0,1,1432288059,1432288764),(5,'探索MOOC的机遇与挑战',2,'||','数字化阅读服务团队','',1432288095,'<h3><strong>一、MOOC发展</strong></h3>\r\n\r\n<p>MOOC是指大规模的在线开发模式（Massive Open Online Course），最早是2008年由Dave Cormier和Bryan Alexander教授提出。MOOC的提出孕育了一场新的教育革命的到来。2011年秋，&ldquo;慕课&rdquo;取得井喷式发展。来自190多个国家的16万人注册了斯坦福大学的《人工智能导论》课程。2012年，被《纽约时报》称为&ldquo;慕课元年，&rdquo;在全球引起了广泛的注意。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>二、MOOC的网络平台</strong></h3>\r\n\r\n<p>随着MOOC风暴在全球的兴起，各国纷纷推出自己的MOOC平台，全球三大网络平台分别为Coursera，Udacity和edx。中国几所知名大学在2013年合作开发了开放教育平台Ewant。</p>\r\n\r\n<p><strong>（一）Coursera</strong></p>\r\n\r\n<p>Coursera，&ldquo;课程的时代&rdquo;，始建于2011年，由美国加州斯坦福大学的两位计算机教授吴恩达（Andrew Ng）和达芙妮&middot;科勒（Daphne Koller）创办，在2012年4月公开推出其专有平台，截止到2013年9月该公司的注册学习人数超过400万，旗下已经有5门课程得到美国教育委员会的官方认可，意味着学生在Coursera完成这5门课程后将有可能转换为大学里相应的学分。</p>\r\n\r\n<p><strong>（二）Udacity</strong></p>\r\n\r\n<p>Udacity成立于2012年，由大卫&middot;史蒂芬斯（David Stavens）和塞巴斯蒂安&middot;史郎（Sebastian Thrun）和迈克尔&middot;索科尔斯基（Michael Sokolsky）共同创办。Udacity的特色在于它的课程由自己的团队建设。截至2012年底，有超过75万名学生注册学习。与其他尝试高等教育的课程不同之处在于，Udacity更注重寓教于练，寓教于听。教授的讲解只是个开始，再引入正题后便由学生主动解决问题，互相探讨。</p>\r\n\r\n<p><strong>（三）ed</strong><strong>x</strong></p>\r\n\r\n<p>edx是麻省理工和哈佛大学于2012年4月联手创建的不以营利为目的在线课堂平台。两所大学在这个非盈利性计划各资助三千万美元。2012年秋天，edx在MITx启动，目前已经发展到所谓的&ldquo;x联盟&rdquo;的28所院校。主要目的是配合校内教学，提高教学质量和推广网络在线教育。据介绍，该计划将整合2所名校师资，推出1个版本，将使10亿人受益。目前已经拥有超过90万的注册者。</p>\r\n\r\n<p><strong>（四）ewant</strong></p>\r\n\r\n<p>Ewant于2013年成立于国立交通大学开放教育资源中心，由国立交通大学、上海交通大学、西安交通大学、西南交通大学、北京交通大学共同合作发起的开放教育平台，课程主要由两岸五校交大提供，于2013年首度开课；修课通过者可以获得交大学习认证。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>三、MOOC给我们带来的机遇</strong></h3>\r\n\r\n<p><strong>（一）MOOC将高校的门槛降低，使学生更易获得优质的教育资源。</strong></p>\r\n\r\n<p>MOOC能够提供大量的优质资源实现资源互享。使不论身居何处的人们都能享受到知识的熏陶，享受优质的课程教学，这是&ldquo;慕课&rdquo;在短时间内大规模风行的原因。从中国目前的国情来说，教学资源分布不均衡，东西部地区、农村与城市地区、普通教育与成人继续教育的教学资源、师资力量差距巨大，最终导致了因缺乏优质资源共享而引起的教育不公平。它可以拆掉大学的&ldquo;围墙&rdquo;，让教育资源不再有时空限制，让优质的教育资源传播到各个角落，能力强的学生可以根据自己的实际情况选择更好的资源，不再局限于个别学校提供的教学条件。</p>\r\n\r\n<p><strong>（二）推动精品课程的升级。</strong></p>\r\n\r\n<p>精品课程是指一流的教学内容、教师队伍、教学方法、教学管理，以及一流的教材等特点的示范性课程。2003年国家开始建设精品课程，2011年教育部启动了精品课程的建设项目。MOOC本身是一个考验学习者坚持性的过程，持续性的在线服务能调动学习者的积极性，解答学习者在学习过程中的疑难问题，激发学习者的学习兴趣，MOOC所具备的这些优势促使精品课程升级为中国式MOOC。</p>\r\n\r\n<p><strong>（三）重塑平等民主的新型师生关系。</strong></p>\r\n\r\n<p>MOOC的颠覆性和革命性的影响将使教师和学生的新型关系得到重构。课程是维护教师和学生的最基本的一个环节，MOOC冲击的除了师生关系这个层面，还有教师与教师之间和学生与学生之间的关系。MOOC更加注重公平公正的民主课堂和亦师亦友的师生关系，促进教师之间的合作，打破教师固步自封的状态，加快教师成长。</p>\r\n\r\n<p><strong>（四）实现了学习过程的灵活性和趣味性。</strong></p>\r\n\r\n<p>MOOC省去了现实课程中一切繁杂的环节，课程设计都围绕如何激发学生学习的兴趣、知识的获得、能力的提高而展开，与此无关的统统都得到精简。特别是成人学生，只需要参与者有一个电子邮箱，就可以在几分钟内完成无门槛的注册，就能参与学习。如果对某一门课程有兴趣，学生甚至还可以在此课程的开课日期尚未确定时就可以通过提前注册&ldquo;预订&rdquo;这门课程。MOOC的视频中，充斥着许多必答的小问题，学习过程环环相扣，具有挑战性。进阶式的学习既可以使学生掌握学习进度，又可以调动学生的积极性，使学生学习上瘾成为&ldquo;课程控&rdquo;欲罢不能。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>四、MOOC在发展过程中面临诸多挑战</strong></h3>\r\n\r\n<p><strong>（一）MOOC给授课教师带来挑战。</strong></p>\r\n\r\n<p>MOOC这种新的教学形式，要求教师从课堂的引导者转变为课堂的导演，教师除了要制作微视频进行教授教学外，还要参与和引导学员之间的互动，教师不再只是信息专家和权威，更多的是教学活动过程的设计与参与者。在教学工作方式上，传统的教学模式从教案设计、备课、教学、到批改作业、课后辅导等都是由教师全程独立完成，而&ldquo;慕课&rdquo;的出现，打破了这种方式，需要依靠教师团队的协作，在课程设计的前期，花费较多的时间和精力来进行课前准备。</p>\r\n\r\n<p><strong>（二）不能做到因材施教。</strong></p>\r\n\r\n<p>因材施教是教学中一项不可或缺的教学方法，在教学中根据不同学生的认知水平，学习能力以及自身素质，教师选择适合每个学生特点的学习方法来有针对性的教学，发挥学生的长处，弥补学生的不足，激发学生学习的兴趣，树立学生学习的信心，从而促进学生全面发展，MOOC对应的大规模学习群体却无法做到，难以因材施教。</p>\r\n\r\n<p><strong>（三）诚信问题与知识产权问题。</strong></p>\r\n\r\n<p>首先，面对社会上&ldquo;替考&rdquo;、&ldquo;作弊&rdquo;现象频出，学习缺乏诚信问题，如何让大规模的学生参加MOOC考试并保持它的信誉？如何保证证书的含金量和可信度？如何利用技术手段杜绝&ldquo;替考&rdquo;或&ldquo;作弊&rdquo;现象？让证书被社会广泛认可，仍有待深入研究。其次，如何解决知识产权保护问题？在《美国版权法》中有&ldquo;正当使用&rdquo;条款，不少国家的版权法也有类似的规定，判断是否&ldquo;正当使用&rdquo;可以根据使用着的目的、作品本身性质和特点、使用多寡和传播数量，以及使用后对版权所有者利益的影响，如何能排除版权尽可能的让所有选课人都能够得到教学所需&ldquo;免费&rdquo;的资源，仍是一大难题。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/\">EduSoho开源网络课堂</a>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/product/mooc\">EduSoho慕课版</a>，中国的慕课！http://www.edusoho.com/product/mooc</p>\r\n','','','','published',5,0,1,0,0,0,1,1432288182,1432288772),(6,'大数据教你怎么让MOOC更吸引人',3,'||','','',1432288203,'<p>&nbsp;</p>\r\n\r\n<p>让学生更投入地学习是教师的责任之一。MOOC的学生流失率一直是关注焦点，开课教师也会绞尽脑汁地吸引、鼓励学生，哪怕只是多学一点点也能让教师的成就感提升。最近读的两篇论文分别基于coursera和edX的数据，从课程制作和授课过程（运营）两个角度分析了如何提升学生的活跃度。有些结论显而易见，有些结论令人惊讶，对开课教师以及平台设计和运营都很有借鉴意义。所以，摘要于此。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><strong>制作更吸引人的课程</strong></h4>\r\n\r\n<p>论文《How Video ProductionAffects Student Engagement: An Empirical Study of MOOC Videos》由MIT的博士生、博士后，及edX工程副总裁共同撰写，发表于应MOOC兴起而新设立，专注于教育与计算机科学交叉学科的会议&mdash;&mdash;ACMLearning@Scale。它分析了edX上690万条视频观看记录，统计得出怎样的视频更吸引人。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>短于</strong><strong>6</strong><strong>分钟的视频最吸引人</strong></p>\r\n\r\n<p>基于edX数据的统计，无论视频多长，用户实际观看时长的中位数都不超过6分钟。而且6-9分钟长的视频是个拐点，更长的视频实际观看中位数反倒会下降。比如长度超过12分钟的视频，实际观看中位数只有3分钟。所以，&ldquo;短视频到底多短最合适&rdquo;这个问题有了标准答案了：6分钟。</p>\r\n\r\n<p>这个数字挺难让人接受的，因为按多数老师的习惯，6分钟根本讲不了什么东西，15分钟都勉强。但和下一个结论联合分析，其实真未必做不到。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>语速快，很关键</strong></p>\r\n\r\n<p>虽然统计数字表明语速和视频吸引力并不完全成正比，但当语速达到每分钟185-254个单词时，无论视频多长，都比低语速能获得更多注意力。原因很好理解，快语速常常伴随着激情，激情富有感染力，感染力可以让学生更专注。所以，教师越热情，甚至是激情，越能吸引学生。</p>\r\n\r\n<p>语速加快，讲同样内容所需时间就会缩短，这样产出的视频就可以向下接近甚至达到6分钟的建议长度了。录课前做好规划，让内容更紧凑，节奏更快，不说废话，不机械重复（学生可以自己重复看），剪掉&ldquo;嗯&rdquo;、&ldquo;啊&rdquo;等口头语，短小精悍的视频就有了。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>教师头像绝非可有可无</strong></p>\r\n\r\n<p>对于大于6分钟的视频，有教师讲课头像的和纯ppt、软件操作等录屏相比，前者收获的关注更多。我想，这是因为头像总在动，比时长处于静止画面的单调录屏更提神吧。我个人确实比较喜欢那种头像嵌入视频一角的形式，但前提是ppt把那一角特意留出来，头像不会遮挡该看到的课件内容。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>制造一对一的感觉</strong></p>\r\n\r\n<p>教师都习惯教室气氛，黑板/大屏幕，站在讲台上，走来走去，甚至安排一些学生假装听众来提起讲课的性质。但数据分析表明，这种在教室/演播室，配置昂贵设备录制的视频，在吸引力上其实不如更低成本的私人录制方式。教师坐着，面对镜头，背景就是办公室，像做单独辅导一样地讲课，效果是最好的。这样很容易产生一种亲切感，而且和坐在电脑前的学生所处学习环境最契合。</p>\r\n\r\n<p>这里的关键点，就是让学生有一对一的感觉。坐下来直面镜头，就基本创造出了这种感觉。语言上再多用&ldquo;你&rdquo;而不是&ldquo;你们&rdquo;，用&ldquo;咱们&rdquo;而不是&ldquo;大家&rdquo;，气氛就有了。很多不谙此道的老师课程开头第一句话总是&ldquo;同学们，大家好&rdquo;，我通常在那一刻就&ldquo;出戏&rdquo;了。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>手写屏</strong><strong>/</strong><strong>笔是最值得购买的设备</strong></p>\r\n\r\n<p>可汗学院的视频是典型的手写笔应用，所以论文干脆将这种视频称为&ldquo;可汗风格&rdquo;。统计表明，与录屏风格相比，学生愿意在可汗风格的视频中投入1.5-2倍的时间。老师边讲边画，确实很像一对一给我讲题的感觉。或者在一片白板/黑板上用板书讲课，或者在ppt上勾画要强调的内容，手写让学生很清楚地知道该看哪里，并忠实地跟着老师的思路。</p>\r\n\r\n<p>用鼠标是很难&ldquo;写&rdquo;出好板书的。手写电磁屏用起来很直观，但也很昂贵。手写笔也够用，而且只卖几百元，非常值得配备一块。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><strong>让学习过程更活跃</strong></h4>\r\n\r\n<p>论文《Engaging with MassiveOnline Courses》由斯坦福大学和康奈尔大学的博士生、教授合作，发表于计算机应用领域世界最顶级的学术会议之一&mdash;&mdash;WWW，并且获得了最佳论文亚军，非常强大。论文通过挖掘coursera上MachineLearning（以下简称ML）和Probabilistic GraphicalModels（以下简称PGM）两门课各三次开课的数据记录，分析了最终成绩不同的学生在学习过程中表现出怎样的差异，以及如何提升课程论坛活跃度。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>课程开始前后选课的用户质量最高</strong></p>\r\n\r\n<p>在课程正式开始前半个月，到课程第一次作业截止这段时间里选课的学生，其中的活跃者比例远高于其它时间选课的。所以，MOOC课程宣传的最佳时机，就是开课前后这个时间窗口，能获取更优质的学员。</p>\r\n\r\n<p>早选课的活跃度低，可能是因为学习热情在等待中消散，或者时间发生了冲突。第一次作业截止时间之后，再努力学也难以获得证书，所以对后续作业等也就不关注了。不过值得一提的是，自开课时间起，哪怕到了课程结束之后，加入到课程但只看视频的学生始终保持着较高的比例。他们虽然不交作业、不考试，但也能从视频有所得，这也是课程价值的体现。所以有些MOOC课程结束后就彻底关闭，连视频都不给人看的做法，是不合适的。</p>\r\n\r\n<p>另外，60%的学生是在课程开始前选课的，所以足够长的预告期还是有必要的。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>学生越活跃，成绩越高</strong></p>\r\n\r\n<p>基本上，学生的最终成绩和他在看视频、做测验、交作业和读论坛的次数是成正比的。也应了那句&ldquo;一分耕耘一分收获&rdquo;的老话。但&ldquo;读论坛&rdquo;这项活动有个独特现象，在80-100分这个区间会变成反比，也就是分数越高，论坛看得越少。这说明学霸对论坛的依赖不高。论坛主要服务于非学霸。</p>\r\n\r\n<p>但不要以为学霸对论坛就没贡献，事实上他们很重要。统计数据表明，占据论坛沙发的学生论坛活跃度都很高（侧面说明人数少），而且他们的平均成绩也在80分之上。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>最大的杀手，是做题</strong></p>\r\n\r\n<p>统计表明，并没有任何视频成为明显的学习终结者，但确实有些作业题目成为障碍，大量的学生被挂在上面，然后就没有然后了。这说明学习中止的很大原因是遇到困难，而非单纯的懒惰。如果能在解题过程多提供一些帮助，可以帮学生走得更远。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>学霸和学渣的帖子有显著区别</strong></p>\r\n\r\n<p>只需要对讨论区做简单的词频统计，就能得出一个结论：交作业多的学生帖子包含更多术语，交作业少的则刷的只是存在感（hello、me、I&rsquo;m、interested是学渣高频词）。如此说来，假如要对学生论坛表现评分的话，那么词频统计的方法也是有一定可行性的。</p>\r\n\r\n<p>另一个区别是帖子的位置。前面说了，学霸喜沙发。那学渣喜什么呢？喜欢提问&hellip;&hellip;其实说提问的是学渣，是不对的。虽然统计表明提问者的平均成绩较低，但还没低到学渣的地步。真正的学渣是连问都不会问的。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>虚荣心是可利用的</strong></p>\r\n\r\n<p>ML的第三次授课在论坛里引入了游戏中常用的勋章系统，结果论坛活跃度提升了不少。而且，勋章的展现越显著（比如紧贴着用户姓名显示），越能刺激活跃度。当然，负面作用也有，就是恶意投票的增加了。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4><strong>总结：教师先洗脑，才能做好在线课程</strong></h4>\r\n\r\n<p>两篇论文（《How Video ProductionAffects Student Engagement: An Empirical Study of MOOC Videos》和​《Engaging with MassiveOnline Courses》）​的分析虽然还谈不上全面、严密，他们自己也坦诚有漏洞。但瑕不掩瑜，这些结论还是很有指导性，并引人思考的。它们坚定了一个观点：一定不要照搬课堂讲课的习惯与模式到在线课程。授课是一项以对象和目标为导向的活动，在线课程的授课对象构成要更复杂（年龄、基础、地域、作息习惯，国际课程还有文化背景差异），学习目的也多样（拿证、深入学习、大体了解、解决手头问题等），再加上学习工具与环境的剧烈变化，可想而知照搬课堂授课的效果会多差。</p>\r\n\r\n<p>现在越来越多的老师开始涉足在线教育。先放下自己赖以成名的教学习惯与观念，用小白的心态重新学习如何在线授课，是扎实的第一步。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/\">EduSoho开源网络课堂</a>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/product/mooc\">EduSoho慕课版</a>，中国的慕课！http://www.edusoho.com/product/mooc</p>\r\n','','','','published',5,0,1,0,0,0,1,1432288258,1432288350),(7,'不加入MOOC的高校将面临失语',3,'||','新华日报','',1432288387,'<p><strong>教师：讲MOOC堪比制作电影大片</strong></p>\r\n\r\n<p>为了将原有课程变成一门&ldquo;MOOC&rdquo;，南大陈昌凯老师完全打破了原有的教学模式，把16周课时的一门课，压缩到了4-6个小时。&ldquo;这极短的时间内，却要浓缩比过去更海量的信息，还要均匀分布&lsquo;刺激点&rsquo;，以保证电脑前的学生不会离开，挑战非常大。&rdquo;从去年10月份开始，陈昌凯和团队一起酝酿，从资料搜集、选题确定、脚本撰写，再到信息呈现的方式，都反复讨论，仔细打磨。陈昌凯说，&ldquo;我学会了用镜头来说话，学会用更有表现力的语言，说出的每一句话都要经得起反复推敲&hellip;&hellip;这样的讲课，堪比制作电影大片了。&rdquo;</p>\r\n\r\n<p>在南京高校，已经有不少学生成为&ldquo;MOOC&rdquo;的忠实粉丝。南大、东大、南师大、南理工等校都有各自的&ldquo;MOOC&rdquo;社团。南京大学软件学院大二学生梅杰正是该校&ldquo;MOOC&rdquo;社团的一员，&ldquo;去年七月份成立以来，我们的组织正在不断扩大，不少同学还拿到了课程相应的认证证书。&rdquo;对于南大新开设的&ldquo;MOOC&rdquo;，梅杰表示期待已久。他刚观看了《马克思的生平和核心著作导论》的一节课，课程开场的&ldquo;酷炫&rdquo;给他留下了深刻的印象。&ldquo;如果马哲课都是这样上，恐怕老师们再也不愁点名啦！&rdquo;梅杰笑道。</p>\r\n\r\n<p>梅杰说，在课堂里，《微积分》这门课程一上就是4节课，想不打哈欠都难。&ldquo;MOOC&rdquo;则相反，仅一小时的&ldquo;MOOC&rdquo;被切割成许多个小视频，每个小视频只有8-10分钟，视频间歇则设计小测验，通关才可以继续学习，&ldquo;就像是打游戏升级一样好玩&rdquo;。</p>\r\n\r\n<p><strong>高校：不加入MOOC将面临失语</strong></p>\r\n\r\n<p>高校越来越意识到，如果不主动加入&ldquo;MOOC&rdquo;潮流，传统的教授课程方式会失去自己的学生。</p>\r\n\r\n<p>&ldquo;同样一门课，学生可能会在网上搜到世界各大学不同的版本，既然&lsquo;MOOC&rsquo;潮流是不可阻挡的，与其让别人影响我们的学生，不如想办法去扩大自己课程的影响。&rdquo;陈昌凯说。有不少高校教师坦言,如今国内高校不少低水平的课程,并不可能一夕之间消亡。但随着国内&ldquo;MOOC&rdquo;平台的壮大,学生有了用脚投票的权利,教师教学水平的重要性也愈加凸显。</p>\r\n\r\n<p>中国著名高校相继融入&ldquo;MOOC&rdquo;平台，背后正是传统大学的危机意识&mdash;&mdash;避免在新一轮教育浪潮中失语。</p>\r\n\r\n<p>与国外相比，目前真正有能力开设&ldquo;MOOC&rdquo;的高校和老师并不多。在江苏，南京地区的高校们频频与MOOC3大平台运营商接触，但加入国际&ldquo;MOOC&rdquo;平台的目前只有南京大学。而东南大学、南京航空航天大学则正按照MOOC运营平台的标准，建设本校的MOOC课程。东大计划在3-5年内建设80&mdash;100门的MOOC课程，南航近期正打造航天特色的8门特色MOOC课程，制作好后会放在工信部建设的高校联盟MOOC平台上。</p>\r\n\r\n<p>南大教师发展中心主任王守仁教授认为，&ldquo;MOOC&rdquo;课堂教师比传统课堂教师要求更高，必须有很强的学术研究背景的支撑，才能&ldquo;化繁为简&rdquo;。南大教务处副处长赵志宏教授说，劝说老师们加入MOOC并不容易，&ldquo;首先&lsquo;MOOC&rsquo;面对的是成千上万的听课者，你对自己课堂质量要有充分的自信；其次，很多老师不习惯对着镜头，平时上课有些小动作、小口误可能都不打紧，上镜头就很难受了。&rdquo;</p>\r\n\r\n<p><strong>机遇：留给中国高校的时间不多了</strong></p>\r\n\r\n<p>赵志宏表示，&ldquo;MOOC&rdquo;浪潮的背后，是中国高校和国外高校教学质量的比拼，只有尽快建立中国版本的&ldquo;MOOC&rdquo;，整合国内优质资源，向所有学子免费开放，才能为中国高等教育留住人才。</p>\r\n\r\n<p>尽管&ldquo;MOOC&rdquo;在国内迎来一片叫好声，但&ldquo;MOOC&rdquo;的制作成本、投入产出效益、技术模式却少有人关注和研究。赵志宏表示，目前一门&ldquo;MOOC&rdquo;的建设费用都在20万元左右，以南大而言，每门课程的成本也在10多万，有些高校甚至为了一门&ldquo;MOOC&rdquo;花费百万。&ldquo;如果就市场而言，只有投入，没有产出，当然不是长久之计。&rdquo;赵志宏认为，从现实角度来看，能够授予证书、文凭甚至学位，是&ldquo;MOOC&rdquo;能够真正市场化运作的前提。然而到目前为止，国内没有一个与三大&ldquo;MOOC&rdquo;平台合作的名牌大学，能为学生提供学分。&ldquo;在这场汹涌的世界浪潮中，中国高水平大学强强联合，积极参与并且融入这场变革，方能化危为机。&rdquo;</p>\r\n\r\n<p>王守仁认为，以&ldquo;MOOC&rdquo;为契机，中国大学要冷静思考如何提高教学质量，而不是简单地赶热闹。&ldquo;仅仅是在线教育，并不能解决高等教育的深层次问题，如何将MOOC教育和传统课堂相结合，去改变教师们课堂教授的方式和内容，才是最根本的应对方法。毕竟留给中国高校提高教学质量的时间，已经不多了。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/\">EduSoho开源网络课堂</a>，最好的网校建校系统， www.edusoho.com；</p>\r\n\r\n<p>点亮万盏灯，照亮求知路。</p>\r\n\r\n<p><a href=\"http://www.edusoho.com/product/mooc\">EduSoho慕课版</a>，中国的慕课！http://www.edusoho.com/product/mooc</p>\r\n','','','','published',2,0,1,0,0,0,1,1432288418,1432288418),(8,'MOOC带来大学课堂改革新思考',3,'||','《教育家》','',1432288454,'<p>&nbsp;</p>\r\n\r\n<p>MOOC早已悄然成为一个全球性的热词。</p>\r\n\r\n<p>2012年被称作是MOOC元年。2013年到2014年，许多中国知名大学也相继加入Coursera、edX等MOOC平台，推出了全球性的在线课程。</p>\r\n\r\n<p>MOOC来了，人们在追问， 中国的大学应该怎么面对？</p>\r\n\r\n<p>有人说这是学习的革命， 也有人认为MOOC教育的认可度和完成率都存在问题； 有人说MOOC将引发大学课堂深刻的变革， 也有人认为MOOC改革并不能解决高校教育资源垄断的根本问题，反而造成马太效应； 而如蒋玉龙们，更多的人则开始了中国式MOOC的实践和探索&hellip;&hellip;</p>\r\n\r\n<p>MOOC与传统课堂能够充分融合吗？几年试验，他已经得到了充分的答案。 对于蒋玉龙，&nbsp;MOOC不仅是一种教学观念的革新， 而更是对网络空间内人的交互关系的重新审视。</p>\r\n\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;</p>\r\n\r\n<p><strong>混合式</strong><strong>MOOC</strong><strong>，大学课堂改革再思考</strong></p>\r\n\r\n<p><strong>&mdash;&mdash;</strong><strong>访复旦大学蒋玉龙教授</strong></p>\r\n\r\n<p><strong>用户体验是一种教育思维</strong></p>\r\n\r\n<p>和蒋玉龙教授见面是在复旦大学一间咖啡馆， 在一栋很新的高楼里。高楼和复旦大学那些折衷主义风格的老建筑形成鲜明对比。</p>\r\n\r\n<p>这位微电子学院的教授开始自我介绍， 语气温和， 又不乏幽默。 记者很快就把视频中的教授切换成实在地坐在眼前的人了。</p>\r\n\r\n<p>&nbsp;&ldquo;互联网产品做得好与不好，就一条，用户体验。用户体验应该不止是互联网思维，而是一种教育思维。但我们常常是把学生的学习体验忽略掉了。 &rdquo;</p>\r\n\r\n<p>蒋玉龙认为，这种学习体验的忽略，在传统的课堂上，主要表现在三个方面：</p>\r\n\r\n<p>其一，在上课之前老师都假设学生会预习。我们认为，只要上课之前预习，到上课时，不懂的地方认真听，懂的地方就可以略过。但在大学课堂上， 学生自身实际很少能做到这一点，有大学排课量饱满、管理松散的原因，也有学生缺乏自控能力的原因。对于自己教授的理工科，蒋玉龙对此感受尤甚。有段时间， 他正式上课之前， 常常会抽问学生， 请他概述这节课要讲的主要内容，但课堂是&ldquo;鸦雀无声&rdquo;的。 即使抽到的， 半天说不清楚， 反而弄得师生尴尬。</p>\r\n\r\n<p>其二，传统的课堂学习和考核常常是不同步的。学习和考核总是存在几个月的时间差， 所以教师对学生状况的了解常常只是远期期盼。教师想问： 学生不懂， 不懂在哪里。 除了极个别拔尖的学生， 老师实际是很难全部了解的。到期末考试分析试卷得出结果，为时已晚。</p>\r\n\r\n<p>其三，广播式灌输式的课堂教学。现在的教学是按照单纯的课时划分，老师已习惯了把所有的教学时间都用来讲授知识。这种单向的一次性的听课过程，缺乏互动。没有互动， 也就没有所谓学习体验。对于理工科，涉及众多公式图表， 实际亦很少有当堂课能完全听懂的。</p>\r\n\r\n<p>&ldquo;所以在课堂里，大家并没有关联感，没有命运共同体的感觉，学生目标离散，教师只是教课。这样，学生对四年学习和毕业后的工作取向实际也是模糊的。 &rdquo; 蒋教授补充道， &ldquo;师生之间的关系其实很脆弱。 因为我们认为，大学的目标是要培养学生独立思考和行动的能力， 大学的老师不可能再像中学老师那样盯着学生。但是，课堂教学的有效性， 我们实际又没有建立起来。&rdquo;</p>\r\n\r\n<p>因此， 探索和推广中国的MOOC， 纳入课堂改革的背景下，蒋教授认为，MOOC必须回答大学教学的基本问题：</p>\r\n\r\n<p><strong>学生为什么来读大学？</strong></p>\r\n\r\n<p><strong>教师教学在大学里有什么作用？</strong></p>\r\n\r\n<p><strong>学校在教、</strong>&nbsp;<strong>学过程中的作用？</strong></p>\r\n\r\n<p><strong>教学过程中，</strong>&nbsp;<strong>教师真的了解学生吗？</strong></p>\r\n\r\n<p><strong>学校了解教师的教学过程吗？</strong></p>\r\n\r\n<p><strong>学校了解学生的需求吗？</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>接触MOOC的大部分学生， 都对这种新的教学形式感到 &ldquo;兴奋&rdquo; 。 事实上， 在互联网快速发展的今天， 学生对这种社交互动的教学模式早已不陌生。</p>\r\n\r\n<p>一位叫谷年龙的学生对记者说：&ldquo;从宽泛的标准说，之前见过的网易公开课或美国TED， 基本都是类似的观念。 &rdquo; 所以， 接受MOOC的学习成本本身是很低的。但是超星平台的交互特性仍然让他们惊讶不已。</p>\r\n\r\n<p>在复旦大学，蒋玉龙也早在2008年就尝试做了所谓&ldquo;移动课堂视频下载精品课程&rdquo;。他将自己上学年讲课的视频录制下来，放到学校的FTP服务器上，然后分发给下届学生。蒋玉龙说，建设MOOC成本最高的部分无非是视频录制，在前几年，要录制视频成本还是很高的， 但现在随着智能设备普及，这个问题已经完全解决了。</p>\r\n\r\n<p>&ldquo;基本上只要你想去做，一台千元智能机也可以拍摄720P视频了。 即使请人摄像也很便宜。 网络硬件水平提高了， 视频的分发渠道和接受渠道实际都解决了。而就MOOC平台的技术水平而言， 建设一门MOOC几乎是傻瓜式的（应用理念是所见即所得的） 。 简单地说， 一个老师只要会上传电子邮件附件， 把一门课所必备的视频文档资料传上去， 一门MOOC就建好了。 &rdquo;</p>\r\n\r\n<p>谷年龙说，班上的同学选蒋老师的课， 大部分首先是喜欢他讲课的风格，简洁、清晰、 明了。 &ldquo;所以看视频课跟实体课差别并不大， 听到的是他的讲解， 看见的是他的PPT， 只是没见到真人而已。&rdquo;而且，有了视频讲义，预习和复习都更加方便。如果第一遍没听懂，视频是完全可以回放的。学习时间就变得非常自由，无论在寝室、教室、 图书馆还是走路、 坐地铁， 随时随地都可以学习。</p>\r\n\r\n<p><strong>交互关系是</strong><strong>MOOC</strong><strong>的核心</strong></p>\r\n\r\n<p>百度搜索时，记者曾在某博客看到一条信息：请问谁有复旦大学蒋玉龙教授半导体物理的视频？这位网友说， 之前优酷上的视频已经不能观看了。</p>\r\n\r\n<p>下面的回答则是：淘宝店铺&ldquo;半导体教育乐园&rdquo;。在淘宝，这套蒋教授的半导体物理视频标价是99元。 这个问题是2013年11月提的。而现在，在&ldquo;超星泛雅平台&rdquo;，这门课已经完全公开发布了。</p>\r\n\r\n<p>视频的公开只是MOOC教育的一部分。正如蒋玉龙讲授的半导体器件，电脑软件如何实现人机交互、人人交互这才是最重要的东西。对于MOOC，他更关注的是， 在这样技术革新的新课堂里， 师生之间的交互关系应该是怎样的？MOOC是否真能规避传统课堂的劣势？传统课堂的优势又能否与MOOC结合起来？</p>\r\n\r\n<p>在早期的在线课堂平台， 基本都是简单的视频分享。 但学生看完视频， 有无效果，懂与不懂，是否能跟上节奏，教师还是看不到的。这样的在线课堂和传统的灌输式课堂并无实质区别。</p>\r\n\r\n<p>一位曾经选过约翰&middot;霍普金斯大学R语言编程课的网友曾写道： 由于该课程仅持续4周， 每周任务量都非常大， 长达3-5小时课程。 学习起来非常吃力，第一堂课他就花了25小时来消化。 最终， 坚持两周， 他还是放弃了。</p>\r\n\r\n<p>MOOC课程一个最关键的问题是知识体系的重新梳理， &ldquo;要有互动，就必须给学生更多的机会和时间。&rdquo;蒋玉龙认为， 教师首先必须调整现有按课时划分知识层级的教学方式， 而改为知识点。表现在视频上，就是尽可能的精简。一个简单的知识点可以控制在5分钟， 一个复杂的知识则可以控制在30分钟以内。</p>\r\n\r\n<p>以他的《半导体物理》为例， 一共有10章40个二级章节。 按传统的授课模式45分钟的课时分割， 必须18周才能完成。但实际只有16个有效教学周。而现在MOOC化之后， 他将整个课程分割为170多个知识点， 每个知识点均控制在5-30分钟。 &ldquo;这些知识点罗列起来， 其实就像字母表、 词条表， 学生很容易进行快速定位和检索。&rdquo;这些视频，在每个学期开学前，他就全部上传到了网络，此后只要按照学习周期推送任务就可以了。</p>\r\n\r\n<p>一方面，按照知识点分割的精简视频，观看方便，学生被各个课程和娱乐分割出来的碎片时间都得到了充分利用。</p>\r\n\r\n<p>另一方面，专门编辑的视频把原本90分钟课时压缩了一半， 学生就有了更多时间自主学习。而在学时安排上，就从原来的每周上课变成了只是双周上实体课。</p>\r\n\r\n<p>空出的一周时间既确保了学生的预习和练习，而在实体课上，传统课堂面对面互动的优势保留了， 并且，不再是老师讲课，而是学生讲课。学生变老师， 老师变导师。</p>\r\n\r\n<p>&ldquo;因为，视频教学的部分不再需要老师在实体课堂上重构了。 &rdquo;</p>\r\n\r\n<p>目前， 复旦大学实行小班教学， 蒋老师的班上只有21个人， 按小组合作学习形式， 在这节150分钟的课堂， 基本每个学生都能得到上台讲课的机会。而且， 已经被精细分割的170多个知识点完全足够每个学生上台展示， 并不会产生视听疲劳。</p>\r\n\r\n<p>一般，蒋玉龙在前两周推送视频学习任务同时，就会分割好教学点给每个小组， 要求每个小组提供一份电子教案。他给学生说，你们的任务就是如何把我讲懂。实体课上蒋玉龙则站在一旁， 当起了&ldquo;学生&rdquo;，听到紧要处，就打断演讲的同学，进行质疑、反问、启发。 &ldquo;这个过程就是锻炼学生的表达能力，应急思考能力，这是比知识更重要的东西。同样地，学生对于知识的把握情况， 我只要问几个问题， 就很清楚了。&rdquo; 蒋教授解释道。</p>\r\n\r\n<p>讲到这里，蒋玉龙突然从笔记本上给记者展示了几个视频，原来都是学生上研讨课的视频。 &ldquo;一开始他们可能不适应，但是几堂课下来，他们很快就讲得口若悬河了。&rdquo;他笑着说， &ldquo;而且问问题的能力也长进了， 你知道在那种情况下， 如果问一些肤浅的问题，他们自己都会觉得不好意思。所以， 问问题之前，他们一定是下了很多功夫思考和预习的。这样我的目的就达到了。&rdquo;</p>\r\n\r\n<p>他指着视频里那些学生，挨个给记者介绍，这个学生表达能力很强，那个学生会讲故事，那个学生问的问题很有创见&hellip;&hellip;当记者请他推荐一个学生采访时，他毫不犹豫就推荐了谷年龙。这个学生曾经在讨论区发布了一条比较深度的评论， 给他印象很深。</p>\r\n\r\n<p>谷年龙后来对记者说，其实，关于这条评论， 更令他受益的是同学之间相互激发的过程。在每个实体课结束前， 蒋老师都会留下一些有难度的问题，要求同学回去看视频讨论。而最近一次，他们在讨论时，却发现了一个新的问题（关于双极性晶体管共射极输出特性曲线），这个问题蒋老师在课堂和视频里并没有细说。所以， 几个同学之间发生了很激烈的争论， 各执一词。 最后， 谷同学在MOOC讨论区写出了这个问题的答案，蒋教授则将之推荐到了文章精华区。</p>\r\n\r\n<p>&ldquo;讨论区还有一点对我帮助很大。因为每个人提问题，实际也是对自己学习情况的一种参照。看别人的问题，你会发现可能有一部分自己并没有完全掌握， 而且这种问题一般都是难点、重点。 &rdquo; 谷年龙说。</p>\r\n\r\n<p><strong>线上线下：数据背后的人</strong></p>\r\n\r\n<p>网上有一段蒋玉龙的采访视频，平时录制了那么多讲课视频，接受视频采访时，他反而显得有些&ldquo;拘谨&rdquo;，还和记者开玩笑说： &ldquo;当演员不容易啊，我以后要去横店学习一下&hellip;&hellip;那边还招群众演员吗？&rdquo;不禁让人哑然失笑。</p>\r\n\r\n<p>在学生面前，蒋玉龙是这样一个谦和、随性的人。录制讲课视频，他必须尽可能地去掉实体课中可能出现的废话，精简视频的形式和长度。而在实体课上， 他必须引导学生去讨论，迫使学生去展现自己的能力，用他的话说就是： 学生变老师， 老师变导师。</p>\r\n\r\n<p>学生在实体课上的表现最后会计入期末考试总分， 占比10%。</p>\r\n\r\n<p>这样， 回答开始提出的大学教学基本问题，他说： &ldquo;大学教育中最大的问题，就是我们的课堂是否满足了学生的意愿，释放了他们学习的潜能&hellip;&hellip;教师和学生应该真正是一个教学相长的过程&hellip;&hellip;知识只是一个载体，而能力才是最重要的， 自控能力、 自学能力、合作能力&hellip;&hellip;而这其中最重要的是自知。教师和学生在整个学习过程中都应该保持一种自知。 &rdquo;</p>\r\n\r\n<p>他觉得平台能提供了一个学生学习全过程的关注体系非常好。这在传统的教学中是不可能做到的， 系统可以将每个学生学习的过程数据都保存在后台，供老师查看。学生观看视频的情况如何， 回答习题的情况如何，参与讨论的情况如何，既有平均数据，也有个体数据。</p>\r\n\r\n<p>这些数据是&ldquo;电子的，但却是实在的&rdquo; ， 因为它记录了学生的学习行为曲线。透过这些数据，他可以精确把握学生真正完成了多少学习任务，理解了多少内容。比如，如果大部分学生都反复观看了同一个视频， 那这个知识点一定对他们是难度的。这样， 他可以随时在线下的实体课上提出补充。或者说，某一段视频学生都是拖着看的， 那就证明这是一段废话。在此后更新视频时， 他会考虑删除或重新制作。</p>\r\n\r\n<p>所以他说： &ldquo;这个数据追踪和反馈体系是对线上线下混合式教学最有价值的内容。 &rdquo;</p>\r\n\r\n<p>说着，蒋教授登入系统后台，给记者展示了两条曲线分布图。一条绿线，一条红线。分别代表学生的复习时间和预习时间。 曲线上的坐标标出了每次实体课的日期（3次）。有趣的是， 红色曲线总是随着实体课到来呈现为峰值状态， 而绿色曲线则是相对平缓的。</p>\r\n\r\n<p>&ldquo;这里可以很清晰地反映出现在学生这种周期性的学习情况。他们的学习是一种功利性状态。 因为明天实体课要做汇报， 所以大部分学生都赶在前一天来做预习了。而复习曲线却是被预习曲线带动的， 因为预习新的知识要相对用到很多以前的知识。&rdquo;</p>\r\n\r\n<p>这样的复习曲线正是实体课的任务驱动才产生的。尽管功利，但它至少改变了学生之前那种期末考试才临时抱佛脚的复习状态。</p>\r\n\r\n<p>但随后，再对比秋季和春季的曲线分布情况，又出现了一个显著变化： 即两个周期之间逐渐出现了小峰值。这个小峰值正是双周不上课那一天（课表上有课）。小峰值的出现表现了学生学习习惯的一种改变。他逐渐保持了一种学习惯性， 功利性削弱了， 自主性则增强了（蒋教授称之为周期性脉冲式学习行为）。</p>\r\n\r\n<p>同样地，在这个体系中，学生也可以看到自己的整个学习情况： 在一段上线时间内视频的观看进度， 习题的正确率， 参与讨论的活跃程度等等。而且，他还可以看到别的同学（匿名的）数据情况以及自己的排名情况（表现在老师的后台，则是知识点的学习情况）。</p>\r\n\r\n<p>&ldquo;知己知彼，高度清醒。为什么一段视频全班平均看了三遍， 我只看了一遍？我是否真的弄懂了？别人发出了什么讨论， 是否也正是我在思考的问题？ 只要你有心去学习，你会反思这些问题。 到了线下和同学讨论或上实体课， 你会着重去学习这些知识点。 &rdquo;</p>\r\n\r\n<p>同时，蒋玉龙给记者展示了一组数据，在2014年9月15日开学之前， 过半的学生已经完成了170多个知识点中的一半或以上。而这批视频是8月5日才公开发布的。 从某种程度上， 系统给出的自我检测体系， 确实有效地激发了学生之间的自学意识。</p>\r\n\r\n<p>在另一组数据中，在专业排名中，蒋玉龙班上的学生在全专业70余人中多数都排在前列。其中， 还可以看到成绩进步的学生和退步的学生。 21人中有11人进步了， 其中7人更是进步了10名以上。</p>\r\n\r\n<p>&ldquo;什么样的人能够得到这样大的进步？中等生。 20%真正好的学生是不用教就能学好的。 而20%成绩差的学生已经丧失了学习动力。60%的大多数，你给了他机会，他是可以进步的， 而这个正是我做（混合式）MOOC最重要的一个目的。 他们是最需要的一个群体。传统的课堂中， 中等生从来是最缺乏互动的&hellip;&hellip;</p>\r\n\r\n<p>&ldquo;而MOOC，只要你想学，它就给你提供了一条充满可能的途径。 &rdquo;</p>\r\n\r\n<p>国外MOOC的三大平台为Coursera、Udacity和edX， 都是集技术、 教学、 评价与交流为一体的综合性平台。 而在中国， 情况则更加复杂。 中国学生希望享用国外的MOOC资源， 但需要突破国内的网络壁垒， 技术门槛和风险都比较高， 由果壳主导的&ldquo;MOOC学院&rdquo;作为自发的国外MOOC学习组织的外延，可以视为国外MOOC的中国大本营， 吸纳的用户也最多。 国内一些高校也希望建立功能全面的网络平台，但在用户数量上无法与国外MOOC平台相比。 而拥有成熟技术的网络平台则努力与高校等教育机构合作，依靠其原有的电子图书馆平台， 将混合式MOOC直接与大学教学过程相融合， 是国内MOOC的崭新尝试。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>本文作者:姚曦&nbsp;杨军</p>\r\n','','','','published',1,0,1,0,0,0,1,1432288484,1432288484),(9,'追问MOOC，前路依旧漫漫',3,'||','','',1434019848,'<p>2012年才兴起的MOOC，虽然仅有短短1年多的发展时间，却已经席卷全球数十个国家。&ldquo;MOOC&rdquo;运动的&ldquo;三驾马车&rdquo;&mdash;&mdash;Udacity（斯坦福大学教授推出的在线课程平台）、Coursera（斯坦福大学教授推出的在线课程平台）和edX引领的&ldquo;MOOC&rdquo;教学发展迅速：Coursera平台已有80多个教育机构提供了400多门课程，有400万名注册用户；Udacity和edX分别有28门和62门课程，有75万和80万名注册用户。</p>\r\n\r\n<p>中国教育发展战略学会会长郝克明表示：&ldquo;美国著名高校引领的大规模网络开放课程，推动了教育发展。信息技术在教育发展和教育改革中的应用，正在改变着传统的教育观念和教学模式，激发和提高了学生的学习兴趣，加强了教师与学生间的互动，促进了教学质量的提高。&rdquo;</p>\r\n\r\n<p>就在线教育界还处于MOOC&ldquo;海啸&rdquo;带来的巨大震动中，向传统教育教学方式发起挑战的MOOC本身，也面临着争议。MOOC发展趋势如何，有哪些难点需要突破，未来将迎接哪些挑战&hellip;&hellip;研究者与实践者对这些问题的多维思考，会为促进信息技术与教育深度融合提供更深刻的启示。</p>\r\n\r\n<p><strong>追问一：在线学习能否进入主流教育？</strong></p>\r\n\r\n<p>&ldquo;最近几十年来信息技术的重大突破和高速发展，为教育带来深刻变革。&rdquo;郝克明特别强调三次技术突破对教育的影响，&ldquo;上世纪80年代，计算机微型化并快速普及，多媒体技术用于教学；上世纪90年代中期，互联网的应用使在线教育得到发展，促进了优质教育资源的传播和共享；第三次是云计算技术的发展和应用，以及各种移动终端的创新和发展，增强了信息的存储、传输以及为学习者服务的能力。&rdquo;</p>\r\n\r\n<p>对于信息技术变革带给教育的影响，爱尔兰国际远程教育资深专家德斯蒙德?基更教授感受颇深&mdash;&mdash;他第一次受邀来中国是在1989年，当时他专程前来参加庆祝中国电大系统成立10周年活动。而他这一次来到中国，则是为了与大家分享&ldquo;MOOC&rdquo;应跟上&ldquo;移动学习&rdquo;步伐的新锐观点。&ldquo;随着无线网络取代有线网络，越来越多的学生不再使用笔记本和台式电脑进行在线学习，而使用智能手机和平板电脑进行移动学习。&rdquo;德斯蒙德?基更说。</p>\r\n\r\n<p>国际数据咨询公司的数据显示，2013年电子设备的发货数据为：1亿3440万个台式电脑，1亿8090万个笔记本，2亿2730万个平板电脑以及101万台智能手机。笔记本和台式电脑的销售数量下降，被智能手机和平板机取代。该公司预测，到2017年，智能手机的发货量将上升到每年170万台，平板机为4亿680万台，笔记本为1亿9660万台，而台式机将下降为1亿2310万台。</p>\r\n\r\n<p>一位在北京的爱尔兰记者报道说，移动手机正成为中国数百万人的口袋图书馆。&ldquo;在北京和上海的地铁里，坐在你对面的人可能看上去正安静地盯着他们的手机，实际上他们正在读惊心动魄的小说。&rdquo;德斯蒙德?基更表示，中国目前已经是世界上移动设备最多的国家。</p>\r\n\r\n<p>&ldquo;如果人们可以在手机屏幕上读小说，他们肯定能通过智能手机读大学学位的课程。&rdquo;在德斯蒙德?基更看来，这种技术变革带来人们习惯的改变，让在线学习向移动学习转变。</p>\r\n\r\n<p>从国际上看，日本政府高度重视移动学习，旨在把日本发展成一个&ldquo;泛在学习&rdquo;社会，使人们广泛使用移动设备来提高福利和进行终身学习。南非的移动学习也非常盛行。中国知名大学开办的68所网络学院和新近成立的国家及部分省市开放大学都在积极开发移动学习。</p>\r\n\r\n<p>联合国教科文组织教育部门教育信息化项目组负责人苗逢春也关注到这种变化，并将这种技术变化称为进入&ldquo;新PC阶段&rdquo;。在他看来，发展中国家的手机用户规模正在迅速追赶发达国家，所以教育信息化必须考虑&ldquo;新PC阶段&rdquo;的特点。</p>\r\n\r\n<p>&ldquo;如果移动学习能够进入主流教育，中国应开始把在线学习材料改造成适合在智能手机和平板机上使用的材料。&rdquo;德斯蒙德?基更说。</p>\r\n\r\n<p><strong>追问二：大数据能否带给教育颠覆性的变革？</strong></p>\r\n\r\n<p>大数据的应用，是当前&ldquo;MOOC&rdquo;的特点，也是未来应加强的内容。这是一些实践者与研究者的观点。</p>\r\n\r\n<p>Coursera发展团队中国区业务负责人伊莱?布林德博士介绍说，他们的平台会给教课的教授提供数据分析工具，通过这个工具，教授能够看到有多少学生看了他发布的视频，学生看了几遍这个视频，教师可以监测学生的表现，及时调节学习内容。他说，若学生不直接使用Coursera平台，而应用翻转课堂，平台上的数据则可以帮助教师甄别哪些是学生已掌握的内容，哪些是学生需要进一步练习的内容。数据分析工具还可以应用于学生开展互评，比如，可以通过数据把那些花了不到一分钟就给出的评价去掉，只取可靠的学生评分。同时，从研究者的角度看，平台记录学生的所有行为，可用于对不同教育问题进行深入研究。</p>\r\n\r\n<p>美国新媒体联盟执行总裁莱瑞?约翰逊博士提供了一种更加深入的观点：&ldquo;MOOC&rdquo;还远远不能被称为学术界的变革创新，这份荣誉属于更具颠覆性和深远性的改革&mdash;&mdash;大数据及其应用。大数据将会让教育机构通过预测工具来提升每个学生的学习成果。这种应用可以在学习过程的每一步收集和整合数据，参看这些数据进行课程设计，可以为学习者量身定制学习任务和学习模块并精准地送达。</p>\r\n\r\n<p>MOOC的发展也将改变教育的环境。基于MOOC的发展前景，北大副教授陈江对未来的教育形态进行了这样的设想：大部分老师成为教学辅助人员，部分老师转而投身科研；制作课程的专营公司会出现；大部分学校倾向提供非常精深、非常实践化的教育内容，以及为其他课程提供场地；判断学校好坏，要看其在网络上有多少选课学生；出现网络学习的第三方考核机构，对于学习效果有官方的认可；雇主可能更认同MOOC证书，而不单看学生毕业于哪所高校；中小学不再盯着升学率，更重视素质教育，更多学生在中学里思考&ldquo;我将来干什么&rdquo;。</p>\r\n\r\n<p>不过，无论是实现&ldquo;MOOC&rdquo;本身的发展，还是实现其对教育的改变，都需要&ldquo;MOOC&rdquo;实践者们的不断探索。伊莱?布林德认为，&ldquo;MOOC&rdquo;至少有很大的规模空间，他很有感触地说，韩国音乐录影带《江南style》在YouTube（美国视频分享网站）上的播放次数达到了近17亿次，若把点击者所有观看的时间加起来，长达2万年,而把当前所有Coursera用户学习&ldquo;MOOC&rdquo;的时间加起来，仅仅是这个数字的三分之一。</p>\r\n\r\n<p><strong>追问三：</strong><strong>MOOC</strong><strong>能否真正促进教育公平？</strong></p>\r\n\r\n<p>虽然风行时间仅一年多，但随着&ldquo;MOOC&rdquo;的急剧扩张与社会各界对&ldquo;MOOC&rdquo;的关注，有关&ldquo;MOOC&rdquo;的不同声音也开始出现。</p>\r\n\r\n<p>这种不同声音首先来自对&ldquo;MOOC&rdquo;促进教育公平的质疑。</p>\r\n\r\n<p>使那些原本无法上大学的群体可以无障碍地学习大学课程，或者使更多人得以享受顶尖的教育资源，这是一些研究者总结出的&ldquo;MOOC&rdquo;带来的颠覆性变化。不过，常年奋战在世界贫困地区一线的苗逢春却对此有着不同的看法。&ldquo;联合国教科文组织主要的服务对象是发展中国家和欠发展国家，所以我们有这个角度的思考，即教育信息化到底能不能为全民增加受教育机会。&rdquo;</p>\r\n\r\n<p>北京开放大学副校长张铁道表示，在教育信息化发展方面，从全球范围看，到目前为止的数字化进程还是&ldquo;有钱人的游戏&rdquo;，那些相对贫困、没有财力购买电脑的群体，以及网络运行没有保障甚至因财政困难付不起电费的学校，则很难参与到数字化教学的变革中来。这就需要国家在制定教育信息化发展规划时，对不同发展水平的群体，在资源投入及预期成效方面做出相应的制度安排。</p>\r\n\r\n<p>不过，对于&ldquo;MOOC&rdquo;与教育公平的关系，教育部科技发展中心主任李志民表示，MOOC提供了一种全新的知识传播和学习方式，其大规模、开放和在线的特点，已经为自主学习者提供了方便灵活的学习机会和广阔空间，但不能要求&ldquo;MOOC&rdquo;解决教育公平的所有问题。</p>\r\n\r\n<p><strong>追问四：</strong><strong>&nbsp;MOOC</strong><strong>能否真正提高学习质量？</strong></p>\r\n\r\n<p>莱瑞约翰逊介绍了Udacity的管理者乔纳森关于&ldquo;MOOC&rdquo;&ldquo;成功率&rdquo;的双重思考：&ldquo;MOOC&rdquo;目前仅有5％至16％的完成率，并不理想，因此，MOOC不可能是传统教育的有效替代品。然而，乔纳森也认为：&ldquo;大规模中的一个小百分比仍然是一个大数目，如Udacity的&ldquo;程序入门&rdquo;课程有60000名学生注册学习，虽然只有14％的通过率，但还有多达23000人完成了课程学习。与之形成鲜明对比的是，在他工作过的4所大学里，&ldquo;程序入门&rdquo;作为大学基础课程，有史以来完成课程学习的学生总数还不到10000人。在管理Udacity的3个月里，他注意到，在这个员工不超过12人的机构里，其经费预算远远低于那4所大学的投入。</p>\r\n\r\n<p>能否真正促进学习质量的提升，这也是一些人对&ldquo;MOOC&rdquo;的质疑。</p>\r\n\r\n<p>对此，莱瑞?约翰逊把关注焦点放在了&ldquo;MOOC&rdquo;是否可以超越讲授式教学。他说：&ldquo;MOOC创始人斯蒂芬?道恩斯和乔治?西门子的设计理念是，大规模在线学习应该区别于传统的教学模式。&rdquo;但莱瑞?约翰逊认为，并非所有的&ldquo;MOOC&rdquo;都实现了最初的设想。每一个&ldquo;MOOC&rdquo;提供者都提出了自己的模型。其中，有些课程采取了大量不同于从前的教学法和工具，包括混合式学习、开放教育资源、交互等，但是，有些课程却仍然沿用传统的讲授式教学模式，仍然维系传统的讲课模式。例如，Coursera就是立足于名牌大学著名专家流行课程的视频讲座，尽管这些内容都是高质的，但其传授方式仍然属于传统的教学方式。</p>\r\n\r\n<p>不过，有专家表示：&ldquo;MOOC刚兴起，任何新事物从出现到完善都要经历一个过程。即使是最先成立的MOOC平台Udacity，到现在也只有两年时间。&rdquo;斯坦福大学校长约翰?轩尼斯将MOOC的发展称为&ldquo;一场数字海啸&rdquo;，他表示：&ldquo;我不能确切地告诉你MOOC会如何变革教育，我的目标是参与进去，而不是静观其变。&rdquo;</p>\r\n\r\n<p><strong>追问五：</strong><strong>&nbsp;MOOC</strong><strong>是否适合中小学？</strong></p>\r\n\r\n<p>&ldquo;MOOC&rdquo;是否适合中小学，技术能否代替教师，这也是一些研究者提出的疑问。</p>\r\n\r\n<p>李志民表示，学习大体可分为三类：第一类是人际交往类的学习，不应该成为学校的主要功能；第二类是知识传承类的学习，应该成为学校的主要功能；第三类是文明发展类的学习，需要系统的基础知识，需要灵感和洞察力、想象力、批判精神，需要相互讨论、启发等，也需要实验场地、仪器设备、模型验证等。从以上学习类别来看，MOOC更适合高等教育，而中小学教育不仅仅是知识的教育，同时更是人格与道德的培养等，MOOC可以作为补充手段，但不适合全课程学习。</p>\r\n\r\n<p>郝克明认为，虽然MOOC主要是在高等教育领域，但对基础教育也有一定的借鉴作用。教师课堂教学的质量与中小学生的学习和成长有着极为密切的关系。而且这种网络学习的方式，还有利于学生把课堂教学的视频从网上下载后反复听，直到听懂为止。</p>\r\n\r\n<p><strong>追问六：</strong><strong>MOOC</strong><strong>面临着哪些致命性的挑战？</strong></p>\r\n\r\n<p>李志民认为，以下这些挑战值得思考：大学是否能提供足够规模的优质课程资源？学分认可可否实现由本校学生带头？盈利还是非盈利？商业模式如何运作？在线学习的文化如何建立？考试与评估如何更权威？新的教育管理评价体系和证书如何被社会广泛接纳？</p>\r\n\r\n<p>李志民说，若&ldquo;MOOC&rdquo;想在中国发展，决策者的重视程度、课程的丰富程度、教师的观念转变和技术应用能力、运营商之间互联互通等需要得到突破。尤其是技术应用能力，我们的MOOC平台华文慕课、学堂在线在应用功能上如何更加符合我国学生的需要，我们的平台搭建商EduSoho们，如何有进行突破性的创新都是我们需要好好思考的。</p>\r\n\r\n<p>伊莱布林德认为，&ldquo;MOOC&rdquo;国际化学习的特点，是&ldquo;MOOC&rdquo;的优势，也是一种挑战。&ldquo;MOOC虽然起源于美国，但60%左右的用户来自非英文国家，30%的用户来自发展中国家。这给更多人带来了新机会，我家乡一名学习中国历史专业的学生，此前无法想到可以通过这种方式学习到北京大学的课程&lsquo;中国历史&rsquo;。&rdquo;伊莱?布林德说，&ldquo;不过实现国际化的理想，在一定程度上取决于学生交流，如果不同母语的学生在语言交流上有障碍，则需要我们在平台上做出更多努力。&rdquo;</p>\r\n\r\n<p>北京开放大学校长胡晓松说，&ldquo;我们近期做了一个调查，得出的结论是：公民在学习当中希望体会到老师、同学是充满活力的，在课堂的交流中能感受社会的感召力，能和老师、同学一起分享知识经验与精神生活，最关键的是，这种学习对工作岗位转变、追求专业发展有帮助。&rdquo;鉴于此，胡晓松提出的有关远程教育的一些思考或许也适用于&ldquo;MOOC&rdquo;面临的挑战：国际上有成功经验，但是我们的市场化够吗？对于这种新的需求，我们的教育工作者能满足吗？以学习者为中心，怎么落到实处？</p>\r\n\r\n<p>&ldquo;教育信息化的高成本、教师的选择、界定教育质量及对教育过程进行评估也存在难点。&rdquo;苗逢春说。</p>\r\n\r\n<p><strong>追问七：</strong><strong>MOOC</strong><strong>的下一站将驶向何处？</strong></p>\r\n\r\n<p>MOOC面临的挑战不仅来自自身发展，也来自在运用信息技术促进教学改革上，已经出现越来越多的其他新鲜经验。大西洋邮报曾有文章这样写道：&ldquo;我们不要将MOOC作为高等教育唯一重要的实验。&rdquo;</p>\r\n\r\n<p>莱瑞约翰逊介绍，一些业内人士提出了&ldquo;反转MOOC&rdquo;的概念，并做出了实践。由美国10所大学组成的联盟宣布了一项在线课程实施计划，在这个项目中，&ldquo;反转&rdquo;的指向是&ldquo;大规模&rdquo;的概念。该项目提供小班报名和学分认证课程，拒绝提供大规模招生。来自任何学校的学生都可以报名学习和缴费。参与项目的大学包括布兰代斯大学、杜克大学、埃默里大学、西北大学等。</p>\r\n\r\n<p>在&ldquo;反转MOOC&rdquo;的各种探索中，新媒体联盟(NMC)与惠普等专业机构建立了合作伙伴关系，组成一个&ldquo;反转MOOC&rdquo;的全面战略联盟，为科学、工程和数学教师提供专业发展的在线课程。同时，这一联盟还建立了一个旨在注重规模、聚焦教育创新和使用媒体来促进学习的模型，例如，通过Facebook（创办于美国的社交网络）展开教学的一门关于社会媒体的课程。</p>\r\n\r\n<p>美国网络解剖公司则在使用高科技支持更有效教学和学习上进行了尝试。这一公司领先开发互动式身临其境的３Ｄ软件。产品包括网络解剖学、虚拟人类尸体，在国际范围用于支持医学院&ldquo;教学&rdquo;和３Ｄ网络教学，一系列的虚拟解剖和模拟因果关系用于支持中小学教学和学习。在美国，这一尝试已经是国家试点项目的一部分，该公司一直参与许多项目来试用３Ｄ技术、互动式白板，采用身临其境模式、互动式软件来研究和衡量这些技术对学生成绩的影响。</p>\r\n\r\n<p>该公司总裁RichardNeil Lineback介绍说，学生有强烈的视觉和动觉学习偏好，通过使用３Ｄ图像，学生兴趣显著增加。在一项由美国国家科学基金会、奥古斯塔纳学院研究院使用网络解剖程序研究互动式3Ｄ课程中，研究小组是前医学院学生。研究发现，这种互动式课程使学生的记忆力增加了33％。</p>\r\n\r\n<p>麻省理工学院也进行了类似的技术创新尝试。据北京开放大学新近出版的《2013新媒体联盟地平线报告》显示，该校采用虚拟实验室技术，吸引学生开展投入性学习。借助网络虚拟实验室，学习者即便在家里也可以借助虚拟体验，获得类似真实实验室的操作实践。虚拟实验室还特别强调学生的即时反馈，并对学生输入的数值进行在线评价，给教学组织者提供反馈信息，使管理者随时了解学生的基本需求和学习状态。</p>\r\n','','','','published',2,0,1,0,0,0,1,1434019883,1434019883),(10,'互联网+，让教育拥抱明天',3,'||','','',1434019953,'<p>国际教育信息化大会部长圆桌会议上，中国教育部副部长杜占元说：&ldquo;在大数据、云计算、移动互联网等新一代技术不断涌现，新的教学模式不断产生的今天，教育信息化不仅肩负着重塑传统的任务，同时也肩负着不断创新的使命，教育信息化是一个需要全球共同面对的课题。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>&ldquo;这是一种高位的均衡&rdquo;</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>无论你身处经历过屠杀和动荡的柬埔寨和卢旺达，抑或是拥有11种官方语言的南非，又或是拥有2.6亿名学生的中国，你都可以和世界上其他60亿人一起分享一块屏幕，学习一种知识，信息的壁垒和鸿沟似乎正随着技术的进步而逐渐消解。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>听起来，这是一个美妙的童话。在中国，这一梦想正在逐渐照进现实。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>梁启超说，&ldquo;欧洲国家，积市而成；中国国家，积乡而成&rdquo;.与发达资本主义国家相比，对于拥有广阔农村地区的中国来说，跨越时间空间的信息化技术，成为解决城乡教育差距的良药--全国6.4万个偏远地区教学点实现数字资源全覆盖，400万名农村孩子享受到了优质的教育资源。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;这实现了从0到1的突破&rdquo;,华中师范大学校长杨宗凯这样评价中国教育信息化基础建设的发展，&ldquo;为教育公平铺平了道路&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;有&rdquo;&ldquo;无&rdquo;的问题解决了，如何提质才是关键。&ldquo;当农村孩子还在盯着崭新的电子白板，接受来自遥远北京的品牌课程时，城市里的孩子已经开始利用信息技术开展探究性学习了，信息化已经融入了他们的思维。在某种意义上，这种鸿沟也在拉大。&rdquo;杨宗凯的言语里充满担忧。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当中国人民大学附中的网络视频课，第一次投射到内蒙古托克托县三中数学教师张军胜的电子白板上时，他先是&ldquo;兴奋异常&rdquo;,然后就经历&ldquo;想吃螃蟹却无处下口&rdquo;的尴尬，&ldquo;两所学校之间的水平差距太大了&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>信息化能否带来教育公平？在这次大会上，这个看似有些矛盾的问题，被各国教育官员认真地讨论。&ldquo;优者愈优，差者愈差&rdquo;的马太效应，也开始在教育信息化中抬头。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>如果说以标准化流水线为代表的第二次工业革命，在教育领域对应的是以课堂和考试为中心的拷贝式的人才培养，那么，第三次工业革命给教育带来的变化则是个性化教育的发展和创新型人才的培养。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在杨宗凯看来，信息化所指向的教育公平，也正是这种以创新性为纲、以个性化为目的教育公平，&ldquo;这是一种高位的均衡&rdquo;.&ldquo;在这种均衡下，最优的并非最好的，最合适的才是最好的。&rdquo;他认为，马拉火车跑赢飞机，在信息化带来的高位均衡中，并非痴人说梦。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>是&ldquo;互联网+教育&rdquo;,不是&ldquo;教育+互联网&rdquo;</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>说起什么是&ldquo;互联网+&rdquo;,国家开放大学校长杨志坚讲了一个故事。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>一次，他去美国圣安东尼奥市出差，准备顺便去看一场美职篮马刺队的比赛。但他却为如何买票、如何订酒店、如何进场犯愁不已。他的朋友摆摆手，掏出手机，&ldquo;用它就搞定了&rdquo;.通过手机预订房间，买票、选座，直到散场，他也没掏出过钱包。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;对于很多发展中国家来说，互联网、信息化更像一种工具；而对于美国等发达国家的人们来说，互联网如同他们的内脏器官，无线网就是他们每日呼吸吐纳的空气。&rdquo;在杨志坚看来，这是数字&ldquo;原住民&rdquo;和数字&ldquo;移民&rdquo;之间的差距。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在线与否，生死存亡。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>1997年，尼葛洛庞帝的《数字化生存》在中国出版，掀起了一股现代科技启蒙的浪潮。当时的中国，互联网网民只有60万人。然而，不到20年后的今天，中国网民数量已经达到6.49亿人。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>如今，一位爱尔兰记者对中国的第一印象是，手机正在成为中国人的口袋图书馆。他说：&ldquo;在北京的地铁上，坐在你对面的人可能看上去正在安静地盯着他们的手机，实际上他们正在全神贯注地读那些专门显示在他们手机上的令人惊心动魄的小说。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;互联网+&rdquo;让终身学习成了瞬息之间即可完成的事情，国家开放大学的&ldquo;五分钟课程&rdquo;让用户可以在一个等公交车的时间完成学习，聚沙成塔的微学习时代已打开了一条门缝。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>如今，&ldquo;互联网+教育&rdquo;成了教育领域最热的词。但是杨宗凯却认为，目前中国的教育信息化仍处在从&ldquo;教育+互联网&rdquo;到&ldquo;互联网+教育&rdquo;转变的过程。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>杨宗凯说：&ldquo;我们现在还处在&lsquo;新瓶装老酒&rsquo;的阶段，虽然电子白板、电子教材都有了，但是在电子化外衣下，信息化并没有重塑教育方式和课堂生态。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他举了一个形象的例子：&ldquo;&lsquo;教育+互联网&rsquo;就像是西红柿炒鸡蛋，炒出来番茄是番茄，鸡蛋是鸡蛋；&lsquo;互联网+教育&rsquo;则是一锅老汤，所有的食材都融化共存在一锅汤里，不分你我。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在未来的教育中，信息技术要与教学深度融合。正如一位参会者所说，&ldquo;再先进的技术、再高级的软件，也无法替代教师的言传身教；再多彩的视频、再精美的画面，也无法替代面对面、手牵手的温暖鼓励&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>MOOC</strong><strong>：从这里，走向哪里？</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;参与过MOOC学习的请举手！&rdquo;在&ldquo;MOOC及其他在线学习的创新方法&rdquo;论坛上，主持人开场就问出了这样的问题。100多人的会场，只举起了五六只手。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;是时候给MOOC泼泼冷水了&rdquo;,英国数字化学习顾问史蒂芬&middot;哈格德将MOOC的过度繁荣比喻为一种数字化的泡沫，&ldquo;我曾做过一项涉及3.5万名学生的调查，如果对免费的MOOC多收1英镑，就会流失掉三分之一的学生&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2015年，由MOOC引发的数字海啸逐渐退潮，一些反对的观点也逐渐出现，奥德丽&middot;沃特斯提出了&ldquo;Anti-MOOC&rdquo;（&ldquo;反MOOC&rdquo;）这一术语，主张通过在线课程激发参与者对话与互动及相互评论，并启动了高层次教学实验。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在北航软件学院院长孙伟看来，目前中国有很多MOOC平台（例如学堂在线、华文慕课等）已经很多MOOC平台搭建服务商(例如EduSoho等）最大的威胁来自学生家长，&ldquo;家长没收了孩子的手机、平板等电子设备，再精美的课程也到达不了学生那里&rdquo;.除此之外，MOOC如何与现行学历认证挂钩，如何制定相应的考评标准、成果认定，如何让企业认同这种学历，如何激励教师的技术水平提升&hellip;&hellip;所有这些因素，都是中国未来MOOC发展需要考量的问题。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>而对像马达加斯加这样的国家而言，MOOC要惠及这里，首先要解决硬件问题，&ldquo;我们的2000多所中学，只有200多所通了电&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>尽管如此，与会者对MOOC在保存和传播人类文化方面做出的卓越贡献还是给予了高度评价，但在未来MOOC往何处发展方面，并没有统一的答案。</p>\r\n','','','','published',2,0,1,0,0,0,1,1434019987,1434019987),(11,'老魏说 ：EduSoho要定义在线教育的服务标准',1,'||','茄葩','',1434020003,'<p>老魏（魏建强）个子不高，微憨的面孔看不出一丝温州人该有的精明。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>如果他不出声的话。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他说话有些嚅嗫，但时有惊人之语，譬如&ldquo;平台就是&lsquo;劣币驱逐良币&rsquo;的过程&rdquo;、&ldquo;教育有三个层次：知识传递、技能传授、道德养成&rdquo;。如果恰巧这时手上有一支烟，那么他的观点能犀利更多。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>站在EduSoho位于善缘街1号的北京分公司办公室往北望，不到500米就是中关村创业大街。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>前不久，国务院总理李克强刚造访过那里。&ldquo;大众创新，万众创业&rdquo;的热潮在方寸之地奔涌升腾，向全国蔓延开去。被裹挟其中，本就红火的在线教育愈发不可收拾。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏身在局中，但处在一个微妙的位置。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>就像善缘街1号，靠近热潮中心，能借势造势；但又保持一定距离，看清热潮涌动下的波诡云谲。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他始终很清醒。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>被团队推着出来二次创业</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏做技术出身。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>还在2003年的时候，老魏就创办了一家公司做SaaS（software as a service，软件即服务，茄葩注） 。那时还没有SaaS的概念，企业也不放心把数据交付给第三方平台，于是2006年这家公司就倒闭了。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏掌握的技术很多是从开源软件中学到的，而美国是开源思想的肇始地。带着朝圣般的虔诚，他在初次创业失败后加入了一家知名的美资企业做项目。但后来发现，美国人做项目也不见得周全规划，常常是往大了做，做出规模就行。他心中的&ldquo;圣殿&rdquo;轰然倒塌。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>告别美企，老魏先后加入杭州19楼和天阙科技，均担任技术总监。在这个岗位上，他整合技术团队，理顺产品流程，带着一批有想法的年轻人日夜奋战，帮助公司实现跨越式发展。但最终,老魏发现他所做的并不是自己喜欢做的事情。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他手底下的团队想和他出来单干。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏开始犹豫。第一次创业失败的经历让他怀疑自己不适合创业，因为除了技术，他别无所长，所以他宁愿寻找下一个雇主，也完全没有另立山头的想法。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>但团队里的年轻人天天游说他一起去创业，老魏越来越招架不住。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;他们都觉得我行，我为什么觉得自己不行?&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2011年4月，老魏的离职申请没有获批，他顶住老板软硬兼施的压力，带着3个人做出了好知网，一个知识分享社区。2012年，老魏正式离职。自诩为&ldquo;看过猪跑&rdquo;的他，深谙互联网公司的运作手段。于是活学活用，为好知网带来了大流量，但苦于无法变现。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;我们没有教育内容资源，也没有to C的市场能力，唯一不缺的是产品技术实力。既然如此，为什么不发挥我们的优势呢？&rdquo;。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2012年5月，阔知科技成立；2013年，EduSoho上线，为培训机构、企业、学校提供在线教育的基础服务，比如一整套的网校解决方案。而自2012年起，国内在线教育开始蓬勃发展；尤其是进入2013年后，在大量风投和私募基金的推动下，新开办的教育网站一度以平均每天2.6家的速度诞生。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>以网校解决方案切入市场的EduSoho赶上了这波风口。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;EduSoho起初没有做大的想法，觉得能养活五六个人的团队就不错了，没想到一炮而红&rdquo;。每三天更新一个版本的速度，口碑传播效应的累积，使得围聚在EduSoho身边的用户群体越来越大。截至2015年5月，已有超过14000家教育机构在使用EduSoho的产品，其中不乏上市教育企业的身影。公司也从最初的五六人发展为70多人，连续两年实现营收和获利增长。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏的二次创业有了不错的开局。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>EduSoho</strong><strong>要定义在线教育的服务标准</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>EduSoho现有的业务主要包括两方面，EduSoho教育云和开源网络课堂。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>开源网络课堂是一个网校组织，通过线上教学可以让机构更好地适应开源系统，并根据用户的提议更好地满足他们的需求。开源网络课堂的代码是开源的，但是服务不开源，EduSoho通过给机构提供高附加值的服务来实现营收。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>而EduSoho教育云是团队独家研发出来的一个云平台，在这里可以充分实现云计算、存储等功能，可以为机构至少减少90%的硬件成本，简单、高效、性价比高是用户选择的普遍原因。在茄葩记者此前的走访中，包括美团云在内不少提供云服务的机构都把EduSoho教育云视为潜在的竞争对手。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;这是一个伪命题&ldquo;，老魏说，&rdquo;教育是需要经营的，我们花了4年时间才打磨出这款产品，并且只专注于教育领域，而其他提供云服务的公司是覆盖多行业的&ldquo;。老魏很自信，他认为自家的教育云在性价比、功能、应用的可行性方面，都处在行业前列。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>目前，EduSoho教育云只针对既有用户中的商业用户开放。在支撑EduSoho自身业务的基础上，逐渐向所有非商业用户开放，直至完全面向市场。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>但EduSoho想要做的不止于此，老魏对在线教育有自己的理解。他认为教育有三个层次，知识传递、技能传授和道德养成，道德养成穿插在第一层次和第二层次当中。EduSoho已经能在技术上实现知识传递层面的教育需求，接下来要构建服务标准化，满足教育更高层次的需求。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他以电子商务为例来解释服务的重要性。淘宝和京东等平台提供的多是实物商品，且实现了数据标准化、服务流程化，人们接受程度很高。而在线教育提供的是虚拟商品，先购&ldquo;物&rdquo;后服务，所以对服务的要求非常高。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;当前的在线教育没有好的数据标准和服务体系去支撑，EduSoho就想把既有的录播、直播、短信、文档、问答等形式都当成服务手段之一，再慢慢定义一些服务，比如说7天退款、一对一答疑、定期直播答疑、线下活动、认证证书、就业指导、进度跟踪等。把这些服务定义出来，进而标准化，就有利于培训机构沉淀和组织团队，用有效的管理模式去提供服务。而对用户来说，通过服务的量化实现教育产品的刻画，他就会对在线教育提供的服务有一个具体的认识，更容易接受、消费。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>EduSoho的目标是每个细分领域都会有与之配套的服务手段，教育机构根据自身特点和规模自由搭配。老魏一点儿也不担心这件事做不成，&ldquo;我们是一个开源组织，拥有庞大的技术生态链，会有很多人加入进来实现它。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>事实上，EduSoho已经在最近推出的&ldquo;ES班级二期&rdquo;功能中进行了服务标准化的尝试。在这个被老魏定义为&ldquo;国内首个提供了系统化学习、个性化学习、学习过程跟踪管理的一整套强大的互动学习功能&rdquo;中，教学服务被固化下来，包括学习计划指导、24小时阅卷点评、24小时作业批改、提问必答、一对一在线答疑、班级活动和就业指导。老师可以根据实际情况选择班级所能提供的服务内容，其中的学习计划、试卷阅卷点评、作业批改、提问回答、班级活动都可以在线完成。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;我们想让EduSoho定义的服务成为市场上的标准&rdquo;，老魏强调。这才是他要下的棋，教育云不过是整盘棋的一个先手。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>打造一个在未来二十年都有事可做的公司</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>标准不是那么好建立的，和渠道合作在所难免。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏介绍说，EduSoho会采用低价策略，和渠道合作迅速铺满在线成人教育市场。铺满以后，微小型机构继续用低价去和腾讯课堂、淘宝教育、百度传课等平台竞争，EduSoho为这部分用户提供一般性服务作为支撑。而对剩下的掌握20%市场份额的大中型机构提供定制化服务，EduSoho的主要盈利也来源于此。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;微小型机构即使一开始没有自有品牌，依靠平台谋生；等它发展好了，也会有脱离平台自立门户的需要，这时候EduSoho就成了他们的出路&rdquo;。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏直言不讳，EduSoho真正要做的还是平台。在他眼中，腾讯课堂、淘宝教育、百度传课等平台，是属于知识传递层次的，还无法实现技能传授等高层次需求，最后生源仍然会被机构导回去。因为教育最重的一块是服务，而服务由机构提供。EduSoho一旦定义了服务标准，就将成为机构不可或缺的一环。&ldquo;谁掌握了机构，谁才是强势的玩家&rdquo;。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他冷静地看着2015年以来的在线教育平台大战：前新东方执行总裁陈向东创办的O2O学习服务电商平台跟谁学创下A轮融资5000万美元的新纪录；YY旗下100教育未来两年要烧10亿投入在线教育平台建设；移动互联网教育产品导购平台决胜网推出决胜通帮助中小机构实现网络营销；而真正平台出身的淘宝教育则宣称要在未来一年帮助至少一万家线下教育机构向线上转型&hellip;&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;他们扮演的都是开路者的角色，让互联网教育能覆盖到那些传统教育覆盖不到的人群，让更多人接触并认识到在线教育，这是非常无畏，非常了不起的。&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏想做成一家在未来二十年都有事可做的公司，为了这个目标他婉拒了一些财务投资和并购邀约。&ldquo;拿了上市公司的钱就要站队，无形中砍掉了一大半市场份额，无异于自废武功；而很多财务投资机构不理解我们的商业模式，钱进来了反而对发展不利&rdquo;。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>他有拒绝的底气，EduSoho早就实现盈利，今年的营收按当前发展也能预期完成。EduSoho在去年5月拿到的天使投资至今一分钱也没有&ldquo;烧&rdquo;。他不想被资本推着走，吹完泡泡就散了，而是想踏踏实实做好计划的事，努力实现个人价值和公司价值，给社会留下点真正有用的东西。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>为什么是二十年？我问老魏。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;因为二十年后我就要退休了啊！&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏1977年生人。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在人生的头一个二十年，他接触了计算机和互联网。还在太原上大学的时候，和室友凑30元钱去网吧用586计算机上一个小时的网，对他来说就是最幸福的事。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>在人生的第二个二十年，他选择互联网创业。等再回太原和同学聚会，觥筹交错间，昔日同窗对他一把年纪还折腾创业不能理解。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>老魏说，他不在乎别人的看法，他就是喜欢做技术。</p>\r\n','','','','published',1,0,0,1,0,0,1,1434020054,1434020054),(12,'职业教育如何“互联网+”',3,'||','','',1434020075,'<p>5月10日，首届&ldquo;职业教育活动周&rdquo;在北京举行了全国启动仪式，克强总理的重要批示也指出：加快发展现代职业教育，是发挥我国巨大人力优势、促进大众创业、万众创新的战略之举。国务院副总理刘延东则进一步指出：技术技能筑牢强国基石，职业教育成就出彩人生，要大力发展职业教育，将其摆在经济社会发展和教育综合改革的突出位置、优先支持，加快构建现代职业教育体系。这是国家加快发展现代职业教育的又一重大举措。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&ldquo;互联网+</strong><strong>&ldquo;融合传统企业&nbsp;</strong><strong>需要大量专业人才</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>影响职业教育的因素有社会、政治、经济、文化、教育等多个方面，其中经济是在影响职业教育的主要因素。经济因素则主要有：GDP增长状况、经济发展水平、经济发展模式、市场的理性化程度、产业结构、企业结构、劳动力市场模式、劳动力失业状况等几个方面。在产业结构方面，现代职业教育制度的建立，规模的大量扩充，以及现代意义上的职业教育课程的建立，在很大程度上得益于工业革命。工业革命使得产业结构由农业为主转向了以工业为主，才使得现代职业教育的发展成为可能。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当前正处于全产业的转型升级大时代，在互联网+战略的推动下，工业4.0正在让生产车间发生着巨大的变化，数据化、智能化、连接化、信息化是将来生产制造车间的主流技术。这同时也对从业职工有了更高的要求，总体上中国的生产制造正在生产智造迈进，这也是第三次工业革命的主要的内容。譬如，机器人技术、人工智能以及3D打印在接下来也会成为工业生产的主流，虽然这几项技术会让从业人员越来越，但对从业人员的技术要求也越来越高，从业人员必须懂的机器人工作原理及流程、人机交互以及3D建模等技术。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>简单来讲，未来劳动密集型企业已经越来越少，取而代之的是精工作业，需要大量的职业教育培训机构培养更多的具备高等技术的人才。这一点，我在《&ldquo;互联网+&rdquo;未来发展十大趋势》中也提到过，职业教育在互联网+时代会是一个大的热门，服务于企业转型升级的中间商将需要大量的懂互联网能具体操作的人才，这个人才缺口将会非常的巨大，有非常可观的市场。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>当前职业教育的几种类型</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>根据相关数据统计，目前已知的融资在天使到C轮之间职业培训类的教育项目在180家以上，跟广义上的在线教育项目相似，目前职业培训基本模式都为工具类、平台类、网校类等几种。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>工具类：工具类是以一个教学工具为出发点的职业教育，即针对某一种职业或者该职业的细分领域做一个工具类的APP。由于操作简单且能满足精准人群的需求，可以在短时间内累积大量用户，譬如有道词典等就是这一类教育。工具类职业教育的特点是小而轻盈，迭代渐进，容易成功，同时达到用户量之后也可以扩展为平台。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>平台类：现在来看，平台型在线教育是各种在线教育发展的主流，不管任何种类的职业教育最终都会成为一个平台。平台可以累积大量的两端用户，也是传统教育进行在线教育试水的首选。平台类的特点是大而通吃，布局未来，缺点是投入大、风险大，需要快速积聚大量的资源。2014倒闭的在线教育机构中，相当一部分是平台型。目前来看，BAT等网络巨头布局的在线教育都是平台型。同时，大型的平台无法针对细分领域进行服务，而小的机构却能够灵活的针对学员实际情况进行反应。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>网校：网校的特点在于，只要具备上网条件即可以在任何时间、任何地点接入互联网，自主地选择学习内容。相对于面授教育而言，网络教育最大限度地突破了教育的时空限制，是师生分离、非面对面组织的教学活动，是一种跨学校、跨地区的教育体制和教学模式。网校可以融合工具类以及平台型等各种类型，在教学对象上也可以是B2B或者B2C等，形式相对比较灵活，同时内容一般比较丰富。网校有综合教育类也有垂直教育类，其中职业教育最受欢迎。据网校平台搭建商EduSoho数据显示，垂直职业教育更容易吸收学员和留住学员。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>职业教育如何把握&ldquo;互联网+</strong><strong>&rdquo;时代</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>从当前经济发展以及教育的大背景来看，以往的职业培训已经不适合&ldquo;互联网+&rdquo;时代。一方面是教学内容上，传统职业教育的内容无法更好的适应当前行业的转型职业人才的需求;另一方面是教学形式上，即便是一些在线职业教育也仍旧沿袭过去的教育方式，对于培训出技术过硬的学生的专业人才的帮助不是很大。这就使得，大部分参与职业培训的人员都无法直接在企业内上岗，仍旧需要在岗培训很久才能真正入职。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;互联网+&rdquo;时代下的的职业教育应该如何做?其实也没有太难，职业培训一方面是通过培训让某些人具备从事某个职业的技巧，另一方面是让企业能够找到引入就能工作的职业人才。基于以上两个方面，要做职业培训至少要做到以下几点：</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>首先要了解市场与需求。你要做的是广义上的职业培训还是某个领域的垂直培训，市场有多大，要为哪些人提供哪些服务，是否能了解这部分人的刚性以及隐性需求。</p>\r\n\r\n<p>其次，搞明白做职业教育需要的资源有哪些。你所把握的资源有哪些，没有的资源通过什么方式如何去获取等等，不要去做只有idea而其他一切条件都不具备的无意义的创业。</p>\r\n\r\n<p>第三，在了解市场、明白用户人群、拥有一定资源的基础上，进一步了解整个产业链的情况。从上游、中游、下游等各环节寻找还没有人做或者所有人都做的不好的点，然后以这个点为突破口做一些有些改变的事，一定是能够解决用户痛点的事。</p>\r\n\r\n<p>第四，本质上都是做电商。在模式方面，还要明白无论你是网校模式，做平台还是靠工具来满足用户的某个点，在本质上最终要做的还是电商，但是前期首先要积累足够的用户，不然什么模式都是白扯。</p>\r\n\r\n<p>第五，除了以上几点，职业教育要尽量与企业直接关联。与企业的衔接越紧密越好，甚至可以由企业的人为学生授课，职业教育的供应链与价值链能否延长乃至下沉到企业内部，是职业教育能否做成功关键。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>从现在主流职业培训教学模式上，基本上体现的是O2O、资源、职业体系等几个关键词。当然更重要的是要做到教学内容符合实际，其实职业培训已经是当前互联网及传统企业都离不开的工作内容。要进一步做好职业培训，还要求将来的职业教育最基本要做到人才梯度、培训体系、产品理念、线下线上O2O渠道等几方面的建设与研究，这也算是&ldquo;互联网+&rdquo;时代对职业教育乃至教育全行业的一个更高标准的要求。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>未来的&ldquo;互联网+教育&rdquo;，会向智慧教育的方向发展，大数据、云计算、人工智能、虚拟现实等都将让在线教育的形式更加多元化与人性化。这同时也要求从事职业教育等在线教育的人也要具备更高的技能。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>以此，不管社会发展到什么时代，职业教育都会是各行业刚需。</p>\r\n','','','','published',1,0,1,0,0,0,1,1434020102,1434020102),(13,'用互联网+的思维探索教育的未来',3,'||','','',1434020115,'<p>想象一下，一个北京公司白领在上班的地铁上，通过手机上了一堂美国普林斯顿大学的企业管理课，一个西藏偏远牧区的孩子蹲在电脑前，听北京黄城根小学的老师讲授音乐课&hellip;&hellip;这是什么？这就是&ldquo;互联网+教育&rdquo;的理想场景，不分国界、不分老幼，随时随地接触全球最好的教育资源。如今，它正在破土而出、蓄势待发。它带来变革、带来冲击，也带来更多新的希望。</p>\r\n\r\n<p><strong>对传统的冲击</strong></p>\r\n\r\n<p>&ldquo;互联网+教育&rdquo;将会打破既有教育体系。在古代，由于师资力量有限，接受教育成为&ldquo;官二代&rdquo;&ldquo;富二代&rdquo;等少数人的专利和特权，绝大多数平民百姓一辈子也没有触摸笔墨的机会，所谓&ldquo;万般皆下品，唯有读书高&rdquo;。近代以来，全球大多数国家开始普及义务教育，学习开始进入寻常百姓家，大部分人能够接受九年的正规学校教育。互联网时代，教育完全突破时空限制，一根网线和一台终端就可以链接全世界的教育课件，全民教育和终身教育成为现实。无论何地，人们都可以利用碎片化的时间从互联网获得教育，是真正意义的&ldquo;活到老，学到老&rdquo;，&ldquo;受教育年限&rdquo;的概念将逐渐消失。</p>\r\n\r\n<p>&ldquo;互联网+教育&rdquo;将重新定义学校和老师。互联网时代，以信息为载体的优质教育资源不再聚集和局限在有限的几所学校，而是可以近乎无成本地均匀分布在每个学校，届时&ldquo;名师&rdquo;的定语不再是学校，&ldquo;择校&rdquo;这个词将会消失。同时，传统教育体制下，教师是学生学习知识的全部世界。&ldquo;互联网+教育&rdquo;环境下，教师更多的是作为一扇可以观看外面风景的窗口。如何引导学生从&ldquo;知之者&rdquo;变成&ldquo;好之者&rdquo;，更进一步变成&ldquo;乐之者&rdquo;将是教师的主要作用。</p>\r\n\r\n<p><strong>认识的误区</strong></p>\r\n\r\n<p>有人认为，&ldquo;互联网+教育&rdquo;就是未来教育的全部，在互联网上可以得到所有课堂上的知识，这是把教育简单地等同于知识的灌输。爱因斯坦说：&ldquo;学校的目标始终应当是：青年人在离开学校时，是作为一个和谐的人，而不是作为一个专家。&rdquo;互联网可以&ldquo;教之以事&rdquo;，却不能&ldquo;喻诸德也&rdquo;，遑论培养高尚的情操和完整的人格。特别是对于义务教育阶段的中小学生，老师面对面情感交流的意义尤为重要，其作用&ldquo;身教重于言传&rdquo;，这显然是单凭互联网无法做到的，线上线下相结合是未来教育的必然。</p>\r\n\r\n<p>有人认为，&ldquo;互联网+教育&rdquo;就是提了好多年的教育信息化，不过是新瓶装旧酒而已。应该看到，不同于之前的教育信息化或者教育互联网，&ldquo;互联网+教育&rdquo;不是简单地把互联网套在传统教育的躯壳上，而是把互联网的理念浇灌和渗透到教育的最深处，努力催生出新的教育模式和方法。互联网的作用不仅仅局限于传统的课件演示、网络课堂和作业、教育管理系统信息化等，而是从&ldquo;育人&rdquo;的根本目的出发，重新考虑如何把互联网作为手段和方法。应该讲，&ldquo;教育互联网&rdquo;和&ldquo;互联网教育&rdquo;具有本质区别。</p>\r\n\r\n<p>有人认为，&ldquo;互联网+教育&rdquo;并不适应中国的教育方式，同时认为，与西方强调优先激发学生学习兴趣的&ldquo;引&rdquo;的教育方式相比，中国强调优先把知识传递给学生的&ldquo;授&rdquo;的教育方式具有很大的优势，有必要强化中国在这方面的教育优势，其实这是一种误区，因为从效果看，&ldquo;授&rdquo;只在基础知识传输上有优势，而在激发学生创造思维上并无优势。以自由学习为特征的&ldquo;互联网+教育&rdquo;毫无疑问会打破中国的这种模式。夯实学生的基础知识固然重要，但是激发学生的学习热情、保持永远的学习激情则更为重要。</p>\r\n\r\n<p><strong>发展的关键</strong></p>\r\n\r\n<p>传统学校和老师如何适应信息领域的新技术和新应用，是决定&ldquo;互联网+教育&rdquo;能否进入正轨的首要问题。毫无疑问，互联网正在逐步瓦解传统的教育体系。作为教育网络中的结点，学校特别是大学和民间培训机构面临巨大的不确定性。学生从无法选择老师到近乎可以从全世界找到自己喜欢的老师，&ldquo;互联网+教育&rdquo;将会&ldquo;消灭&rdquo;大多数平庸的老师。国外甚至有人断言：&ldquo;未来全球只需要三所大学：哈佛大学、耶鲁大学和牛津大学。&rdquo;本质上讲，教育互联网领域的这种&ldquo;马太效应&rdquo;是一种社会进步的表现，它可以从教育领域解放更多的人力去从事其他有益工作。学校和老师必须以互联网的意识和心态去平和面对现实。</p>\r\n\r\n<p>&ldquo;互联网+教育&rdquo;如何吸引全社会资源投入其中并形成良性循环也至关重要。互联网信息近乎零成本扩散和传递的属性，使得互联网教育难以像传统培训机构那样依靠教室的物理实体来限制知识&ldquo;外泄&rdquo;，从而实现重复授课并获利。目前，即便是全球最有名的美国Udacity、Coursera和edX等三大MOOC也没有找到合适的商业模式，从事互联网教育的企业如何实现持续盈利成为当前难以破解的难题，而作为我国的互联网教育企业，像EduSoho、学堂在线、华文慕课等，如何持续创新，也是&ldquo;互联网+教育&rdquo;能否实现可持续发展的关键。解决这一问题，还需要从互联网本身的逻辑去剖析。回看互联网的发展历史，成功的互联网企业从来就没有直接依靠&ldquo;出售&rdquo;信息或者提供信息&ldquo;通道&rdquo;获利的，谷歌、雅虎、亚马逊如此，百度、网易、阿里巴巴、腾讯也是如此，他们都是采取所谓&ldquo;羊毛出在狗身上&rdquo;的策略，以免费聚人气，再在人气上做文章。&ldquo;互联网+教育&rdquo;或者教育的互联网化，要想实现快速且可持续发展，就不能从传统教育方式中找寻答案，而是应从互联网思维的角度去探索未来。</p>\r\n','','','','published',1,0,1,0,0,0,1,1434020134,1434020134),(14,'精品资源共享课程与MOOC的碰撞',3,'||','数字化阅读服务团队','',1434020145,'<p><span style=\"font-size:16px\"><strong>精品资源共享课程建设背景</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>由国家教育部主导、全国高等院校广泛参与的精品课程建设，到现在已经有十多年了。作为开放教育资源运动的一部分，我国的精品课程建设取得了较为明显的成效。精品课程量多面广，吸引了众多教师和学生的关注，为推动教学改革创新及优质资源共享做出了贡献。但是，精品课程在建设中也逐渐显现出了课程资源不丰富、课程共享性不强、网站开发更新不足、师生交互性不够等问题。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ldquo;十二五&rdquo;期间，教育部提出将精品课程转型升级为精品资源共享课程。精品资源共享课程是精品课程的继承和发展，是在原精品课程建设成果的基础上优化结构、转型升级。大学英语作为一门重点的全国通用公共基础课程，其资源建设和共享的需求甚为迫切。在总结前期精品课程建设经验和教训的基础上，建设大学英语精品资源共享课程，对于巩固大学英语精品课程建设成果、推动大学英语教学的改革创新和转型发展有着重要的作用。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>精品资源共享课程与</strong><strong>MOOC</strong><strong>的比较</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>精品资源共享课程与MOOC的建设目标是相似的，都是为学习者提供优质的课程资源，建设具有完善教学环节的在线课程，促进教育教学观念的转变和教学方法的变革与创新，服务学习型社会建设，但两者在以下四个方面有着一些差异。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>1.</strong><strong>教学资源</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>我国精品资源共享课程以大量的基础课和专业课为重点，以课程资源系统、丰富和适合网络传播为基本要求，经过国家、省、校三级建设，形成普通本科教育、高等职业教育、网络教育等多层次、多类型的优质课程教学资源共建共享体系，为高校师生和社会学习者提供优质课程教学资源。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>MOOC是一个国际性的教育平台，国内外众多知名大学的加入，使得它吸收了不同国家、不同语言和不同文化背景的课程，课程内容覆盖了从自然科学到人文科学的众多学科。MOOC打破了学校的&ldquo;围墙&rdquo;，甚至是国家的&ldquo;围墙&rdquo;，让优质的教育资源不受时间和空间限制，为学习者提供给一个更为丰富和便捷的知识仓库。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>2.</strong><strong>教学规模</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>精品资源共享课程是以高校教师和学生为服务主体，同时面向社会学习者开放。但国家精品课程资源中心发布的统计数据显示，虽然近几年注册使用精品课程的人数在不断上升，但相对于全国高校师生总数而言，使用人数相当少，社会学习者注册和使用率就更低了。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>而MOOC以其大规模著称，如2011年美国斯坦福大学的《人工智能导论》课程，吸引了来自全球的16万名学生注册学习。MOOC还具有开放性强和组织灵活的特点，它的授课对象非常广泛，从学生到工人、农民、公司职员或退休人员，凡是想学习的都可以参与到MOOC中，根据自己的学习兴趣和时间来进行学习。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>3.</strong><strong>教学设计</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>精品资源共享课程采用自主学习和教师指导教与学相结合的模式。学习者经由课程导学系统进入各教学单元的学习，通过阅读电子教案或课件、观看教学视频进行课程学习，再通过作业系统进行练习，最后还可以通过考试系统进行自测。但是，很多课程的教学视频往往是教师的课堂教学实况录像，时间较长，重点不突出，容易使学习者失去学习积极性。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>MOOC与以往的网络公开课不同，不再仅仅是视频授课，而是实现了教学课程的全程参与。在MOOC这个平台上，学习者可以完成上课、做作业、分享观点、参加考试、获得分数、甚至是拿到证书的整个过程。MOOC中的视频一般只有10分钟左右，知识点更细化，既符合了学习者的最佳注意力时长，又能帮助学习者抓住学习重点。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>4.</strong><strong>互动交流与评价</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>精品资源共享课程的互动交流模块或答疑模块，经常设在每个课程网站的首页，主要采用BBS在线问答或电子邮件的方式，学习者在学习中有疑难，可以观看前人留下的讨论发言帖、也可以自己发帖提出问题，或是按网站提供的地址给教师发送电子邮件。但这些交流和答疑方式，存在提问和回答时间的延误，有一定的局限性，不能进行畅通的沟通，不能及时解决学习者的疑难问题，会影响学习者的学习积极性和主动性。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>MOOC则提供了方便的交流平台，在每个视频的旁边都设有对应的讨论区，学习者在学习过程中遇到问题，可以随时在讨论区提出问题，与其他学习者一起讨论解决问题，或是得到授课教师或助教的解答。MOOC还结合运用机器判分和学习者互评的方式，对学习者的课程学习情况、作业完成情况和课程最终考试进行评价，学习者可以及时了解自己的知识学习和掌握情况。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>对于&nbsp;MOOC&nbsp;而言，平台和课程都是必不可少的组成部分。平台问题可以选择入住，比如coursera、华文慕课、学堂在线等；平台搭建也可以选择EduSoho慕课等平台搭建服务商。而就内容而言，无论是精品资源共享课程还是MOOC课程，都是极为有利于我们更新教育教学观念，探索新的建设与使用模式。</p>\r\n','','','','published',4,0,1,0,0,0,1,1434020212,1434020212),(15,'世界那么大，后台那么新，教你如何不迷失~',1,'||','','',1434020236,'<div>\r\n<div>\r\n<p><span style=\"font-size:16px\">为了让整个网校后台能够更加便捷的进行管理，我们在本次的EduSoho 5.4.0版本中对后台进行了</span><span style=\"font-size:20px\"><span style=\"color:rgb(51, 204, 153)\"><strong>全面升级</strong></span></span><span style=\"font-size:16px\">，重新梳理调整了一些菜单和功能的位置。具体的调整内容有如下五点：</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:20px\"><span style=\"color:rgb(51, 204, 153)\">一、</span>调整</span></strong><span style=\"font-size:16px\">了一级导航，更为简洁清晰。</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">&ldquo;班级&rdquo;整合到了&ldquo;课程&rdquo;下；</span></li>\r\n	<li><span style=\"font-size:16px\">&ldquo;应用&rdquo;整合到了新的&ldquo;教育云&rdquo;下；</span></li>\r\n	<li><span style=\"font-size:16px\">&ldquo;内容&rdquo;、&ldquo;小组&rdquo;整合到了新的&ldquo;运营&rdquo;菜单下。</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-17/1026146ce44a276067.png?5.3.2\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"color:rgb(51, 204, 153)\"><strong>二、</strong></span><strong>统一</strong></span><span style=\"font-size:16px\">菜单功能组织逻辑，<em><strong>设置就只管设置，运营就只管运营</strong></em>，区分内容运营和功能设置。</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">所有功能设置都放置于&ldquo;系统&rdquo;菜单下；</span></li>\r\n	<li><span style=\"font-size:16px\">跟教育云相关的都放置于&ldquo;教育云&rdquo;菜单下；</span></li>\r\n	<li><span style=\"font-size:16px\">所有内容运营的功能（包括内容条目、记录、统计、推荐、公告等）都放置于&ldquo;用户&rdquo;、&ldquo;课程&rdquo;、&ldquo;运营&rdquo;等菜单下。</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">这样整理后，将功能根据职责来区分更容易理解，更方便管理员找到相关的功能，同时也为以后的功能扩展、权限系统打下了基础。</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-17/102914a1435e128698.png?5.3.2\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"color:rgb(51, 204, 153)\"><strong>三、</strong></span><strong>梳理</strong></span><span style=\"font-size:16px\">一些二级导航，进行了调整，逻辑上更为清晰，功能更容易查找。</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-17/14041603081a870206.png?5.3.2\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"color:rgb(51, 204, 153)\">四、</span>新增&ldquo;常用功能</strong></span><span style=\"font-size:16px\"><span style=\"font-size:20px\"><strong>&rdquo;</strong></span>的便捷设置。只要把某个功能页面添加到&ldquo;常用功能&rdquo;，您就可以在后台任何位置快速跳转到相关的页面，省却了繁琐的路径记忆和多次点击。</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-17/1031175816af570066.png?5.3.2\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\"font-size:20px\"><span style=\"color:rgb(51, 204, 153)\">五、</span>新增&ldquo;网校公告</span></strong><span style=\"font-size:16px\"><strong><span style=\"font-size:20px\">&rdquo;</span></strong>，发布的公告将显示在网校首页顶部导航下，支持超链接，支持展示多个公告，让您能够把一些重要信息方便的通知到所有访问网站的用户。</span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-17/14420919a1c0533975.png?5.3.2\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"color:rgb(51, 204, 153)\">六、</span></strong>新版后台结构图</span></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">1、管理后台</span></strong></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/1029222c9e0f533740.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>2、用户</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/1030582f08d6268490.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>3、课程</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/103111fad3f4250730.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>4、运营</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/1031280ac6d8012190.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>5、订单</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/103140cba6bd607883.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>6、账务</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/103608852126780909.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>7、教育云</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/103625949bbf602788.png?5.3.2\" /></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>8、系统</strong></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://www.qiqiuyu.com/files/default/2015/04-22/103642a10ce0253586.png?5.3.2\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>&nbsp;分享到</div>\r\n\r\n<div>&nbsp;</div>\r\n','','','http://www.qiqiuyu.com/files/default/2015/04-17/1026146ce44a276067.png?5.3.2','published',18,0,0,1,0,0,1,1434020304,1434020304);
INSERT INTO `article_category` (`id`, `name`, `code`, `weight`, `publishArticle`, `seoTitle`, `seoKeyword`, `seoDesc`, `published`, `parentId`, `createdTime`) VALUES (1,'EduSoho','edusoho',0,1,'EduSoho网络课堂新闻中心','EduSoho 在线教育 网校系统 ','EduSoho网络课堂是面向个人、学校、培训机构及企业用户的友好、开源、高性价比的在线教育建站系统。EduSoho提供在线教学、网校管理、云视频点播、直播、移APP等功能，为使教育机构能够以最低成本开展在线教学活动，EduSoho也提供云服务器，云视频、云直播和移动APP定制服务，并配备相应的技术支持服务，让客户搭建自有品牌网校和搭积木一样简单。EduSoho产品及服务大大降低了传统教育培训机构进入在线教育行业的门槛。与其他在线教育产品相比，EduSoho具有完整，高自由度，高性价比的产品体验，使用其搭建在线教学网站是快速进入在线教育的最佳选择方案。EduSoho开源并免费提供给非商业用途用户使用，用户可在3分钟内搭建功能完备的在线教育网站。',1,0,1429865619),(2,'默认分类','default',10,1,'','','',1,0,1429865709),(3,'行业资讯','news',0,1,'','','',1,0,1432287805);
