INSERT INTO `groups` (`id`, `title`, `about`, `logo`, `backgroundLogo`, `status`, `memberNum`, `threadNum`, `postNum`, `ownerId`, `createdTime`) VALUES (1,'ES学习圈','<p>试试在小组里发话题、回复话题、回复别人的回复、收藏话题、分享话题吧~</p>\n','public://user/2015/04-23/1822411bfe8e903668.png','','open',6,18,0,1,1429784503),(2,'作业大讲堂','<p><strong>EduSoho系统下如何解决学生答疑问题？</strong></p>\n\n<p><strong>网站中提供三种方式进行答疑：</strong></p>\n\n<p><strong>1、每个课程和班级都有一个讨论区，讨论区可以给教师提问题，系统会提醒老师解答问题；也可以发普通话题与同学们互动；</strong></p>\n\n<p><strong>2、网站首页的客服系统：QQ、QQ群、电话、邮箱、微信等方式都可以答疑。客服设置在【管理后台】-【系统】-【站点设置】-【客服设置】；</strong></p>\n\n<p><strong>3、小组功能，小组功能可以说是将课程和班级独立出来，但功能又不限于课程，可以与全站所有用户交流，更广泛，比较适合组建兴趣圈子和组织活动。</strong></p>\n','public://default/2015/06-18/0904088801e0997753.jpg','','open',2,1,0,1,1433922595),(3,'一起去玩吧','<p><strong>EduSoho系统下如何解决学生答疑问题？</strong></p>\n\n<p><strong>网站中提供三种方式进行答疑：</strong></p>\n\n<p><strong>1、每个课程和班级都有一个讨论区，讨论区可以给教师提问题，系统会提醒老师解答问题；也可以发普通话题与同学们互动；</strong></p>\n\n<p><strong>2、网站首页的客服系统：QQ、QQ群、电话、邮箱、微信等方式都可以答疑。客服设置在【管理后台】-【系统】-【站点设置】-【客服设置】；</strong></p>\n\n<p><strong>3、小组功能，小组功能可以说是将课程和班级独立出来，但功能又不限于课程，可以与全站所有用户交流，更广泛，比较适合组建兴趣圈子和组织活动。</strong></p>\n','public://default/2015/06-18/090621d7eec5090928.jpg','','open',1,0,0,1,1433922656),(4,'慕课讨论区','<p><strong>EduSoho系统下如何解决学生答疑问题？</strong></p>\n\n<p><strong>网站中提供三种方式进行答疑：</strong></p>\n\n<p><strong>1、每个课程和班级都有一个讨论区，讨论区可以给教师提问题，系统会提醒老师解答问题；也可以发普通话题与同学们互动；</strong></p>\n\n<p><strong>2、网站首页的客服系统：QQ、QQ群、电话、邮箱、微信等方式都可以答疑。客服设置在【管理后台】-【系统】-【站点设置】-【客服设置】；</strong></p>\n\n<p><strong>3、小组功能，小组功能可以说是将课程和班级独立出来，但功能又不限于课程，可以与全站所有用户交流，更广泛，比较适合组建兴趣圈子和组织活动。</strong></p>\n','public://default/2015/06-10/155158e4340f775236.jpg','','open',2,1,0,1,1433922693),(5,'网站课程动态','<p><strong>EduSoho系统下如何解决学生答疑问题？</strong></p>\n\n<p><strong>网站中提供三种方式进行答疑：</strong></p>\n\n<p><strong>1、每个课程和班级都有一个讨论区，讨论区可以给教师提问题，系统会提醒老师解答问题；也可以发普通话题与同学们互动；</strong></p>\n\n<p><strong>2、网站首页的客服系统：QQ、QQ群、电话、邮箱、微信等方式都可以答疑。客服设置在【管理后台】-【系统】-【站点设置】-【客服设置】；</strong></p>\n\n<p><strong>3、小组功能，小组功能可以说是将课程和班级独立出来，但功能又不限于