INSERT INTO `category` (`id`, `code`, `name`, `icon`, `path`, `weight`, `groupId`, `parentId`, `description`) VALUES (1,'fuction','使用教程','','',1,1,0,''),(2,'default','默认分类','','',10,1,0,''),(3,'intro','产品介绍','','',0,1,0,''),(4,'administrator','管理员','','',10,1,1,'本分类介绍管理员如何管理网校。'),(5,'teacher','教师','','',20,1,1,'本分类介绍教师如何在网校教学。'),(6,'student','学员','','',30,1,1,'本分类介绍学员如何在网校学习和交流。');
